import { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";

// ---------- Component Imports ----------
import Home from "./components/Home";

// ---------- Farmer Components ----------
import FarmerLogin from "./components/FarmerLogin";
import FarmerSignup from "./components/FarmerSignup";
import FarmerDashboard from "./components/FarmerDashboard";
import AddProduct from "./components/AddProduct";
import ProductList from "./components/ProductList";

// ---------- Retailer Components ----------
import RetailerLogin from "./components/RetailerLogin";
import RetailerSignup from "./components/RetailerSignup";
import RetailerDashboard from "./components/RetailerDashboard";
import RetailerCart from "./components/RetailerCart";
import RetailerOrders from "./components/RetailerOrders";
import PaymentPage from "./components/PaymentPage";
import FarmerProducts from "./components/FarmerProducts";

import "./App.css";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);
  const [userType, setUserType] = useState(null);

  // ---------- Load auth state on refresh ----------
  useEffect(() => {
    const auth = localStorage.getItem("isAuthenticated");
    const userData = localStorage.getItem("user");
    const type = localStorage.getItem("userType");

    if (auth === "true" && userData && type) {
      setIsAuthenticated(true);
      setUser(JSON.parse(userData));
      setUserType(type);
    }
  }, []);

  // ---------- LOGIN ----------
  const login = (userData, type) => {
    setIsAuthenticated(true);
    setUser(userData);
    setUserType(type);
    localStorage.setItem("isAuthenticated", "true");
    localStorage.setItem("user", JSON.stringify(userData));
    localStorage.setItem("userType", type);
  };

  // ---------- LOGOUT ----------
  const logout = () => {
    setIsAuthenticated(false);
    setUser(null);
    setUserType(null);
    localStorage.clear();
    sessionStorage.clear();
  };

  // ---------- Protected Route ----------
  const ProtectedRoute = ({ children, allowedType }) => {
    const auth = localStorage.getItem("isAuthenticated");
    const storedUserType = localStorage.getItem("userType");

    if (auth !== "true") {
      return <Navigate to={`/${allowedType}/login`} replace />;
    }

    if (storedUserType !== allowedType) {
      return <Navigate to={`/${storedUserType}/dashboard`} replace />;
    }

    return children;
  };

  return (
    <Router>
      <Routes>
        {/* ---------- HOME ---------- */}
        <Route
          path="/"
          element={
            <Home
              isAuthenticated={isAuthenticated}
              user={user}
              userType={userType}
              logout={logout}
            />
          }
        />

        {/* ---------- FARMER ROUTES ---------- */}
        <Route
          path="/farmer/login"
          element={
            !isAuthenticated ? (
              <FarmerLogin login={(data) => login(data, "farmer")} />
            ) : (
              <Navigate to="/farmer/dashboard" replace />
            )
          }
        />
        <Route
          path="/farmer/signup"
          element={
            !isAuthenticated ? (
              <FarmerSignup />
            ) : (
              <Navigate to="/farmer/dashboard" replace />
            )
          }
        />
        <Route
          path="/farmer/dashboard"
          element={
            <ProtectedRoute allowedType="farmer">
              <FarmerDashboard user={user} logout={logout} />
            </ProtectedRoute>
          }
        />
        <Route
          path="/farmer/add-product"
          element={
            <ProtectedRoute allowedType="farmer">
              <AddProduct />
            </ProtectedRoute>
          }
        />
        <Route
          path="/farmer/products"
          element={
            <ProtectedRoute allowedType="farmer">
              <ProductList />
            </ProtectedRoute>
          }
        />

        {/* ---------- RETAILER ROUTES ---------- */}
        <Route
          path="/retailer/login"
          element={
            !isAuthenticated ? (
              <RetailerLogin login={(data) => login(data, "retailer")} />
            ) : (
              <Navigate to="/retailer/dashboard" replace />
            )
          }
        />
        <Route
          path="/retailer/signup"
          element={
            !isAuthenticated ? (
              <RetailerSignup />
            ) : (
              <Navigate to="/retailer/dashboard" replace />
            )
          }
        />
        <Route
          path="/retailer/dashboard"
          element={
            <ProtectedRoute allowedType="retailer">
              <RetailerDashboard user={user} logout={logout} />
            </ProtectedRoute>
          }
        />
        <Route
          path="/retailer/cart"
          element={
            <ProtectedRoute allowedType="retailer">
              <RetailerCart />
            </ProtectedRoute>
          }
        />
        <Route
          path="/retailer/orders"
          element={
            <ProtectedRoute allowedType="retailer">
              <RetailerOrders />
            </ProtectedRoute>
          }
        />
        <Route
          path="/payment"
          element={
            <ProtectedRoute allowedType="retailer">
              <PaymentPage />
            </ProtectedRoute>
          }
        />

        {/* ---------- DEFAULT ---------- */}
        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>
    </Router>
  );
}

export default App;
