import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import "./FarmerDashboard.css";

const FarmerDashboard = ({ onLogout }) => {
  const [farmer, setFarmer] = useState(null);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      // Fetch farmer profile
      const profileResponse = await fetch("http://localhost:8080/api/farmer/profile", {
        credentials: "include",
      });

      if (profileResponse.ok) {
        const farmerData = await profileResponse.json();
        setFarmer(farmerData);
      }

      // Fetch products
      const productsResponse = await fetch("http://localhost:8080/api/products/my-products", {
        credentials: "include",
      });

      if (productsResponse.ok) {
        const productsData = await productsResponse.json();
        setProducts(productsData);
      }
    } catch (error) {
      console.error("Error fetching dashboard data:", error);
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = async () => {
    try {
      await fetch("http://localhost:8080/logout", {
        method: "POST",
        credentials: "include",
      });
      onLogout();
      navigate("/login");
    } catch (error) {
      console.error("Logout error:", error);
    }
  };

  const getRecentProducts = () => {
    return products.slice(0, 4);
  };

  if (loading) {
    return <div className="loading">Loading your dashboard...</div>;
  }

  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        <div className="header-content">
          <h1>FarmChain Tracker</h1>
          <nav className="nav-links">
            <Link to="/dashboard" className="nav-link active">
              Dashboard
            </Link>
            <Link to="/products" className="nav-link">
              My Products
            </Link>
            <button onClick={handleLogout} className="logout-btn">
              Logout
            </button>
          </nav>
        </div>
      </header>

      <main className="dashboard-main">
        <div className="welcome-section">
          <h2>Welcome back{farmer ? `, ${farmer.firstName}` : ""}!</h2>
          <p>Manage and track your farm products on the blockchain</p>
        </div>

        <div className="stats-cards">
          <div className="stat-card">
            <h3>Total Products</h3>
            <p className="stat-number">{products.length}</p>
          </div>
          <div className="stat-card">
            <h3>Active Products</h3>
            <p className="stat-number">
              {products.filter((p) => !p.harvestDate).length}
            </p>
          </div>
          <div className="stat-card">
            <h3>Harvested</h3>
            <p className="stat-number">
              {products.filter((p) => p.harvestDate).length}
            </p>
          </div>
          <div className="stat-card">
            <h3>Farm Location</h3>
            <p className="stat-location">{farmer?.location || "Not set"}</p>
          </div>
        </div>

        <div className="recent-products">
          <div className="section-header">
            <h3>Recent Products</h3>
            <Link to="/products" className="view-all-link">
              View All â†’
            </Link>
          </div>

          {products.length === 0 ? (
            <div className="empty-state">
              <p>No products added yet.</p>
              <Link to="/products" className="add-product-btn">
                Add Your First Product
              </Link>
            </div>
          ) : (
            <div className="products-grid">
              {getRecentProducts().map((product) => (
                <div key={product.productId} className="product-card">
                  <h4>{product.productName}</h4>
                  <p className="product-type">{product.productType}</p>
                  <p className="product-id">
                    ID: {product.productId.substring(0, 8)}...
                  </p>
                  {product.blockchainHash && (
                    <p className="blockchain-hash">
                      Blockchain: {product.blockchainHash.substring(0, 16)}...
                    </p>
                  )}
                  <div className="product-status">
                    <span
                      className={`status-badge ${
                        product.harvestDate ? "harvested" : "growing"
                      }`}
                    >
                      {product.harvestDate ? "Harvested" : "Growing"}
                    </span>
                  </div>
                  <Link
                    to={`/products/${product.productId}`}
                    className="view-details-link"
                  >
                    View Details
                  </Link>
                </div>
              ))}
            </div>
          )}
        </div>

        {farmer && (
          <div className="farmer-info">
            <h3>Farm Information</h3>
            <div className="info-grid">
              <div className="info-item">
                <strong>Farm Name:</strong> {farmer.farmName}
              </div>
              <div className="info-item">
                <strong>Location:</strong> {farmer.location}
              </div>
              <div className="info-item">
                <strong>Email:</strong> {farmer.email}
              </div>
              <div className="info-item">
                <strong>Phone:</strong>{" "}
                {farmer.phoneNumber || "Not provided"}
              </div>
            </div>
          </div>
        )}
      </main>
    </div>
  );
};

export default FarmerDashboard;
