import React, { useState } from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";

const TransportSignup = () => {
  const [form, setForm] = useState({
    name: "",
    email: "",
    password: "",
    phone: "",
    vehicleNo: "",
  });
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await axios.post("http://localhost:8080/api/transporters/signup", form);
      alert("‚úÖ Registration successful! Redirecting to login...");
      navigate("/transport/login");
    } catch (err) {
      console.error("Signup failed:", err);
      alert("‚ùå Signup failed. Please try again later.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-container">
      <h1>üöõ Transporter Registration</h1>
      <form onSubmit={handleSubmit}>
        <input name="name" placeholder="Full Name" onChange={handleChange} required />
        <input name="email" type="email" placeholder="Email" onChange={handleChange} required />
        <input name="password" type="password" placeholder="Password" onChange={handleChange} required />
        <input name="phone" placeholder="Phone Number" onChange={handleChange} required />
        <input name="vehicleNo" placeholder="Vehicle Number" onChange={handleChange} required />
        <button type="submit">{loading ? "‚è≥ Registering..." : "Register"}</button>
      </form>
      <p>
        Already registered? <Link to="/transport/login">Login here</Link>
      </p>
    </div>
  );
};

export default TransportSignup;
