import React, { useEffect, useState, useCallback } from "react";
import { useNavigate } from "react-router-dom";
import { cartAPI, orderAPI } from "../services/api";
import "./RetailerCart.css";

const RetailerCart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [retailer, setRetailer] = useState(null);
  const [total, setTotal] = useState(0);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  // ‚úÖ Fetch cart data
  const fetchCart = useCallback(async (retailerId) => {
    try {
      const res = await cartAPI.getCart(retailerId);
      const data = Array.isArray(res.data) ? res.data : []; // defensive check
      setCartItems(data);
      calculateTotal(data);
    } catch (err) {
      console.error("Error fetching cart:", err);
      setCartItems([]);
    } finally {
      setLoading(false);
    }
  }, []);

  // ‚úÖ Calculate total amount
  const calculateTotal = (items) => {
    if (!Array.isArray(items)) return setTotal(0);
    const totalPrice = items.reduce(
      (sum, item) => sum + (item.product?.price || 0) * (item.quantity || 0),
      0
    );
    setTotal(totalPrice);
  };

  // ‚úÖ On page load
  useEffect(() => {
    const stored = JSON.parse(localStorage.getItem("retailer"));
    if (!stored) {
      alert("Please log in to view your cart.");
      window.location.href = "/retailer/login";
      return;
    }
    setRetailer(stored);
    fetchCart(stored.id);
  }, [fetchCart]);

  // ‚úÖ Remove item
  const handleRemove = async (itemId) => {
    if (!window.confirm("üóëÔ∏è Remove this item from your cart?")) return;
    try {
      await cartAPI.removeItem(itemId);
      const updated = cartItems.filter((i) => i.id !== itemId);
      setCartItems(updated);
      calculateTotal(updated);
    } catch (err) {
      console.error("Error removing item:", err);
      alert("‚ùå Failed to remove item.");
    }
  };

  // ‚úÖ Clear all items
  const handleClearCart = async () => {
    if (!retailer) return;
    if (!window.confirm("‚ö†Ô∏è Are you sure you want to clear your entire cart?")) return;
    try {
      await cartAPI.clearCart(retailer.id);
      setCartItems([]);
      setTotal(0);
      alert("üßπ Cart cleared successfully!");
    } catch (err) {
      console.error("Error clearing cart:", err);
      alert("‚ùå Failed to clear cart.");
    }
  };

  // ‚úÖ Buy a single product
  const handleBuyNow = async (item) => {
    if (
      !window.confirm(
        `üí∞ Buy ${item.product.name} from ${item.product.farmer?.name || "this farmer"}?`
      )
    )
      return;

    try {
      const res = await orderAPI.placeOrder(retailer.id);
      alert(
        `‚úÖ Order placed successfully with ${item.product.farmer?.name || "the farmer"}!`
      );
      navigate("/payment", {
        state: {
          order: {
            id: res?.data?.id || retailer.id,
            totalAmount: item.product.price * item.quantity,
          },
        },
      });
    } catch (err) {
      console.error("Error placing order:", err);
      alert("‚ùå Failed to place order. Try again.");
    }
  };

  // ‚úÖ Place all orders
  const handlePlaceOrder = async () => {
    if (!window.confirm("‚úÖ Confirm placing your full order?")) return;
    try {
      const res = await orderAPI.placeOrder(retailer.id);
      alert("‚úÖ Order placed! Redirecting to payment...");
      setCartItems([]);
      setTotal(0);
      navigate("/payment", {
        state: {
          order: { id: res?.data?.id || retailer.id, totalAmount: total },
        },
      });
    } catch (err) {
      console.error("Error placing order:", err);
      alert("‚ùå Failed to place order. Try again.");
    }
  };

  if (loading) return <p className="loading">Loading your cart...</p>;

  // ‚úÖ Group products by farmer (optional, cleaner view)
  const groupedByFarmer = cartItems.reduce((groups, item) => {
    const farmerName = item.product?.farmer?.name || "Unknown Farmer";
    if (!groups[farmerName]) groups[farmerName] = [];
    groups[farmerName].push(item);
    return groups;
  }, {});

  return (
    <div className="cart-page">
      <div className="cart-header">
        <h1>üõí Your Shopping Cart</h1>
        {retailer && (
          <p>
            Welcome, <b>{retailer.storeName || retailer.name}</b>
          </p>
        )}
      </div>

      {cartItems.length === 0 ? (
        <div className="empty-cart">
          <h2>üõçÔ∏è Your cart is empty</h2>
          <p>Browse products and add them to your cart to continue shopping.</p>
          <a href="/retailer/dashboard" className="back-btn">
            ‚Üê Back to Dashboard
          </a>
        </div>
      ) : (
        <>
          {/* ‚úÖ Grouped by farmer */}
          {Object.entries(groupedByFarmer).map(([farmerName, items]) => (
            <div key={farmerName} className="farmer-section">
              <h2 className="farmer-title">üë®‚Äçüåæ {farmerName}</h2>
              <table className="cart-table">
                <thead>
                  <tr>
                    <th>Product</th>
                    <th>Quantity</th>
                    <th>Price (each)</th>
                    <th>Total</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {items.map((item) => (
                    <tr key={item.id}>
                      <td>{item.product?.name}</td>
                      <td>{item.quantity}</td>
                      <td>‚Çπ{item.product?.price}</td>
                      <td>‚Çπ{(item.product?.price * item.quantity).toFixed(2)}</td>
                      <td>
                        <div className="cart-actions">
                          <button
                            onClick={() => handleRemove(item.id)}
                            className="remove-btn"
                          >
                            ‚ùå Remove
                          </button>
                          <button
                            onClick={() => handleBuyNow(item)}
                            className="buy-btn"
                          >
                            üí∞ Buy
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ))}

          <div className="cart-summary">
            <div className="summary-details">
              <h3>Total Amount:</h3>
              <h2 className="price-tag">‚Çπ{total.toFixed(2)}</h2>
            </div>
            <div className="summary-actions">
              <button onClick={handlePlaceOrder} className="place-btn">
                ‚úÖ Place All Orders
              </button>
              <button onClick={handleClearCart} className="clear-btn">
                üßπ Clear Cart
              </button>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default RetailerCart;
