import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { farmerAPI } from "../services/api";


const FarmerManageOrders = () => {
  const navigate = useNavigate();
  const [orders, setOrders] = useState([]);
  const [farmer, setFarmer] = useState(null);
  const [loading, setLoading] = useState(true);

  // Load farmer from localStorage
  useEffect(() => {
    const storedFarmer = JSON.parse(localStorage.getItem("farmer"));
    if (!storedFarmer) {
      navigate("/farmer/login");
      return;
    }
    setFarmer(storedFarmer);

    loadOrders(storedFarmer.id);
  }, [navigate]);

  const loadOrders = async (farmerId) => {
    try {
      const res = await farmerAPI.getFarmerOrders(farmerId);
      setOrders(res.data || []);
    } catch (err) {
      console.error("Error loading farmer orders:", err);
      alert("Failed to load orders");
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <p className="loading">‚è≥ Loading orders...</p>;

  return (
    <div className="farmer-orders-container">
      <h1>üìú Manage Orders</h1>
      <p>Orders placed on your products</p>

      {orders.length === 0 ? (
        <p className="empty">No orders yet.</p>
      ) : (
        <table className="orders-table">
          <thead>
            <tr>
              <th>Order ID</th>
              <th>Date</th>
              <th>Total</th>
              <th>Retailer</th>
              <th>Status</th>
              <th>View</th>
            </tr>
          </thead>

          <tbody>
            {orders.map((order) => (
              <tr key={order.id}>
                <td>{order.id}</td>

                <td>{new Date(order.createdAt).toLocaleString()}</td>

                <td>‚Çπ{order.totalAmount?.toFixed(2)}</td>

                <td>{order.retailer?.name || "N/A"}</td>

                <td>{order.status}</td>

                <td>
                  <button
                    className="view-btn"
                    onClick={() => navigate(`/farmer/order/${order.id}`)}
                  >
                    üîç View
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default FarmerManageOrders;
