import React, { useState, useEffect } from "react";
import { productAPI } from "../services/api";
import "./ProductList.css";

const ProductList = ({ refreshKey }) => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  // ‚úÖ Fetch products for the logged-in farmer
  const fetchProducts = async () => {
    setLoading(true);
    setError("");

    try {
      // ‚úÖ Get logged-in farmer
      const farmer = JSON.parse(localStorage.getItem("farmer"));
      if (!farmer) {
        alert("‚ö†Ô∏è Please log in as a farmer to view your products.");
        setLoading(false);
        return;
      }

      // ‚úÖ Fetch products for this farmer
      const response = await productAPI.getFarmerProducts(farmer.id);
      console.log("‚úÖ Products fetched for farmer:", response.data);
      setProducts(response.data);
    } catch (err) {
      console.error("‚ùå Fetch products error:", err);
      const message =
        err.response?.data?.message ||
        err.message ||
        "Failed to load your products.";
      setError(message);
    } finally {
      setLoading(false);
    }
  };

  // ‚úÖ Run fetch when component loads or new product added
  useEffect(() => {
    fetchProducts();
  }, [refreshKey]);

  // ‚úÖ Get proper image path
  const getImageUrl = (imagePath) => {
    if (!imagePath) return null;
    if (imagePath.startsWith("http")) return imagePath;
    if (imagePath.startsWith("/uploads/")) {
      return `http://localhost:8080${imagePath}`;
    }
    return `http://localhost:8080/uploads/${imagePath}`;
  };

  // ‚úÖ Loading state
  if (loading) return <div className="loading">Loading your products...</div>;

  // ‚úÖ Error state
  if (error)
    return (
      <div className="error-message">
        <pre style={{ whiteSpace: "pre-wrap", color: "red" }}>
          ‚ùå {error}
        </pre>
      </div>
    );

  // ‚úÖ Empty list
  if (products.length === 0) {
    return (
      <div className="no-products">
        <h3>No products found</h3>
        <p>Start by adding your first product!</p>
      </div>
    );
  }

  // ‚úÖ Render product cards
  return (
    <div className="product-list-container">
      <div className="product-list-header">
        <h2>üß∫ My Products</h2>
      </div>

      <div className="products-grid">
        {products.map((product) => {
          const imageUrl = getImageUrl(product.imagePath);
          return (
            <div key={product.id} className="product-card">
              {/* ‚úÖ Product Image */}
              <div className="product-image-container">
                {imageUrl ? (
                  <img
                    src={imageUrl}
                    alt={product.name}
                    className="product-image"
                    onError={(e) => {
                      console.error(`‚ö†Ô∏è Failed to load image: ${imageUrl}`);
                      e.target.style.display = "none";
                      e.target.nextSibling.style.display = "flex";
                    }}
                  />
                ) : null}
                <div
                  className={`image-placeholder ${
                    imageUrl ? "hidden" : ""
                  }`}
                >
                  <span>No Photo</span>
                </div>
              </div>

              {/* ‚úÖ Product Info */}
              <div className="product-info">
                <h3 className="product-name">{product.name}</h3>

                <div className="product-details">
                  <div className="detail-row">
                    <span className="detail-label">Category:</span>
                    <span className="detail-value">{product.category}</span>
                  </div>
                  <div className="detail-row">
                    <span className="detail-label">Price:</span>
                    <span className="detail-value price">‚Çπ{product.price}</span>
                  </div>
                  <div className="detail-row">
                    <span className="detail-label">Batch No:</span>
                    <span className="detail-value">{product.batchNumber}</span>
                  </div>
                  <div className="detail-row">
                    <span className="detail-label">Production Date:</span>
                    <span className="detail-value">
                      {new Date(product.productionDate).toLocaleDateString()}
                    </span>
                  </div>
                  {product.description && (
                    <div className="detail-row description-row">
                      <span className="detail-label">Description:</span>
                      <span className="detail-value description">
                        {product.description}
                      </span>
                    </div>
                  )}
                  {product.createdAt && (
                    <div className="detail-row">
                      <span className="detail-label">Added On:</span>
                      <span className="detail-value">
                        {new Date(product.createdAt).toLocaleDateString()}
                      </span>
                    </div>
                  )}
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default ProductList;
