import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";

export default function UserRetailerProducts() {
  const { retailerId } = useParams();
  const navigate = useNavigate();
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStock = async () => {
      try {
        const res = await axios.get(
          `http://localhost:8080/api/retailers/${retailerId}/stock`
        );
        setProducts(res.data);
      } catch (err) {
        console.error("Failed to load retailer products:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchStock();
  }, [retailerId]);

  const handleBuy = async (productId) => {
    try {
      const currentUser = JSON.parse(localStorage.getItem("user"));

      if (!currentUser) {
        alert("Please login first!");
        navigate("/user/login");
        return;
      }

      const payload = {
        userId: currentUser.id,
        productId,
        quantity: 1,
      };

      await axios.post("http://localhost:8080/api/user/buy", payload);

      alert("Product purchased successfully!");

      // refresh stock after buying
      setProducts((prev) =>
        prev.map((p) =>
          p.id === productId ? { ...p, quantity: p.quantity - 1 } : p
        )
      );
    } catch (err) {
      console.error("Purchase failed:", err);
      alert("Purchase failed. Try again.");
    }
  };

  if (loading) return <h3>Loading products...</h3>;

  return (
    <div className="retailer-products">

      <button className="back-btn" onClick={() => navigate(-1)}>
        ← Back
      </button>

      <h2>Products from Retailer #{retailerId}</h2>

      {products.length === 0 ? (
        <p>No products available from this retailer.</p>
      ) : (
        <div className="product-grid">
          {products.map((p) => (
            <div className="product-card" key={p.id}>
              {p.imagePath && (
                <img
                  src={`http://localhost:8080/uploads/${p.imagePath}`}
                  alt={p.name}
                  className="product-image"
                />
              )}

              <h4>{p.name}</h4>
              <p><strong>Category:</strong> {p.category}</p>
              <p><strong>Price:</strong> ₹{p.price}</p>
              <p><strong>Stock:</strong> {p.quantity}</p>

              <button
                disabled={p.quantity <= 0}
                onClick={() => handleBuy(p.id)}
              >
                {p.quantity > 0 ? "Buy" : "Out of Stock"}
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
