import React, { useState } from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";

const TransportLogin = () => {
  const [form, setForm] = useState({ email: "", password: "" });
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const res = await axios.post("http://localhost:8080/api/transporters/login", form);
      localStorage.setItem("transporter", JSON.stringify(res.data));
      localStorage.setItem("isAuthenticated", "true");
      localStorage.setItem("userType", "transporter");
      alert("‚úÖ Login successful!");
      navigate("/transport/dashboard");
    } catch {
      alert("‚ùå Invalid credentials. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-container">
      <h1>üöõ Transporter Login</h1>
      <form onSubmit={handleSubmit}>
        <input name="email" type="email" placeholder="Email" onChange={handleChange} required />
        <input name="password" type="password" placeholder="Password" onChange={handleChange} required />
        <button type="submit">{loading ? "‚è≥ Logging in..." : "Login"}</button>
      </form>
      <p>
        New here? <Link to="/transport/signup">Signup</Link>
      </p>
    </div>
  );
};

export default TransportLogin;
