import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Home from './components/Home';
import FarmerLogin from './components/FarmerLogin';
import FarmerSignup from './components/FarmerSignup';
import FarmerDashboard from './components/FarmerDashboard';
import './App.css';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [farmer, setFarmer] = useState(null);

  useEffect(() => {
    const auth = localStorage.getItem('isAuthenticated');
    const farmerData = localStorage.getItem('farmer');
    if (auth === 'true' && farmerData) {
      setIsAuthenticated(true);
      setFarmer(JSON.parse(farmerData));
    }
  }, []);

  const login = (farmerData) => {
    setIsAuthenticated(true);
    setFarmer(farmerData);
    localStorage.setItem('isAuthenticated', 'true');
    localStorage.setItem('farmer', JSON.stringify(farmerData));
  };

  const logout = () => {
    setIsAuthenticated(false);
    setFarmer(null);
    localStorage.removeItem('isAuthenticated');
    localStorage.removeItem('farmer');
  };

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route path="/" element={<Home isAuthenticated={isAuthenticated} farmer={farmer} logout={logout} />} />
          <Route 
            path="/login" 
            element={!isAuthenticated ? <FarmerLogin login={login} /> : <Navigate to="/dashboard" />} 
          />
          <Route 
            path="/signup" 
            element={!isAuthenticated ? <FarmerSignup /> : <Navigate to="/dashboard" />} 
          />
          <Route 
            path="/dashboard" 
            element={isAuthenticated ? <FarmerDashboard farmer={farmer} logout={logout} /> : <Navigate to="/login" />} 
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
