import React, { useState, useEffect } from 'react';
import { shipmentAPI } from '../services/api';
import './TransportShipments.css';

const TransportShipments = ({ transport }) => {
  const [shipments, setShipments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [updatingStatus, setUpdatingStatus] = useState(null);

  useEffect(() => {
    fetchShipments();
  }, [transport]);

  const fetchShipments = async () => {
    try {
      const response = await shipmentAPI.getByTransport(transport.id);
      setShipments(response.data);
      setError('');
    } catch (error) {
      setError('Error fetching shipments: ' + (error.response?.data || error.message));
      console.error('Fetch shipments error:', error);
    } finally {
      setLoading(false);
    }
  };

  const updateShipmentStatus = async (shipmentId, status, notes = '') => {
    setUpdatingStatus(shipmentId);
    try {
      await shipmentAPI.updateStatus(shipmentId, { status, notes });
      await fetchShipments(); // Refresh the list
    } catch (error) {
      setError('Error updating shipment: ' + (error.response?.data || error.message));
    } finally {
      setUpdatingStatus(null);
    }
  };

  const getStatusBadge = (status) => {
    const statusClasses = {
      PENDING: 'status-pending',
      PICKED_UP: 'status-picked-up',
      IN_TRANSIT: 'status-in-transit',
      DELIVERED: 'status-delivered',
      CANCELLED: 'status-cancelled'
    };

    const statusLabels = {
      PENDING: 'Pending',
      PICKED_UP: 'Picked Up',
      IN_TRANSIT: 'In Transit',
      DELIVERED: 'Delivered',
      CANCELLED: 'Cancelled'
    };

    return <span className={`status-badge ${statusClasses[status]}`}>{statusLabels[status]}</span>;
  };

  const getNextAction = (currentStatus) => {
    const actions = {
      PENDING: ['PICKED_UP'],
      PICKED_UP: ['IN_TRANSIT'],
      IN_TRANSIT: ['DELIVERED'],
      DELIVERED: [],
      CANCELLED: []
    };
    return actions[currentStatus] || [];
  };

  if (loading) return <div className="loading">Loading shipments...</div>;
  if (error) return <div className="error-message">{error}</div>;

  return (
    <div className="transport-shipments">
      <div className="shipments-header">
        <h2>My Shipments</h2>
        <button onClick={fetchShipments} className="refresh-btn">
          Refresh
        </button>
      </div>

      {shipments.length === 0 ? (
        <div className="no-shipments">
          <p>No shipments assigned yet.</p>
          <p>Check available shipments to get started!</p>
        </div>
      ) : (
        <div className="shipments-list">
          {shipments.map(shipment => (
            <div key={shipment.id} className="shipment-card">
              <div className="shipment-header">
                <h3>Shipment #{shipment.trackingNumber}</h3>
                {getStatusBadge(shipment.status)}
              </div>

              <div className="shipment-details">
                <div className="detail-section">
                  <h4>Product Information</h4>
                  <p><strong>Product:</strong> {shipment.productName}</p>
                  <p><strong>From:</strong> {shipment.farmerName}</p>
                  <p><strong>To:</strong> {shipment.retailerName}</p>
                </div>

                <div className="detail-section">
                  <h4>Route Information</h4>
                  <p><strong>Pickup:</strong> {shipment.pickupAddress}</p>
                  <p><strong>Delivery:</strong> {shipment.deliveryAddress}</p>
                  <p><strong>Estimated Delivery:</strong> {new Date(shipment.estimatedDelivery).toLocaleString()}</p>
                </div>

                <div className="detail-section">
                  <h4>Timeline</h4>
                  <p><strong>Created:</strong> {new Date(shipment.createdAt).toLocaleString()}</p>
                  {shipment.pickupTime && (
                    <p><strong>Picked Up:</strong> {new Date(shipment.pickupTime).toLocaleString()}</p>
                  )}
                  {shipment.deliveryTime && (
                    <p><strong>Delivered:</strong> {new Date(shipment.deliveryTime).toLocaleString()}</p>
                  )}
                </div>

                {shipment.notes && (
                  <div className="detail-section">
                    <h4>Notes</h4>
                    <p>{shipment.notes}</p>
                  </div>
                )}

                <div className="shipment-actions">
                  <h4>Update Status</h4>
                  <div className="action-buttons">
                    {getNextAction(shipment.status).map(nextStatus => (
                      <button
                        key={nextStatus}
                        onClick={() => updateShipmentStatus(shipment.id, nextStatus)}
                        disabled={updatingStatus === shipment.id}
                        className={`action-btn ${nextStatus.toLowerCase().replace('_', '-')}`}
                      >
                        {updatingStatus === shipment.id ? 'Updating...' : `Mark as ${nextStatus.replace('_', ' ')}`}
                      </button>
                    ))}
                    {shipment.status === 'DELIVERED' && (
                      <span className="completed-text">Shipment Completed</span>
                    )}
                    {shipment.status === 'CANCELLED' && (
                      <span className="cancelled-text">Shipment Cancelled</span>
                    )}
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default TransportShipments;
