import React, { useState } from "react";
import { transportAPI } from "../services/api";

const AssignTransport = ({ orderId, transporterId }) => {

  const [form, setForm] = useState({
    assignedDate: "",
    driverName: "",
    contact: "",
    vehicleNo: ""
  });

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const assign = async () => {
    try {
      await transportAPI.assign(orderId, transporterId, form);
      alert("Transport Assigned Successfully!");
    } catch (err) {
      console.error(err);
      alert("Failed to assign transport");
    }
  };

  return (
    <div>
      <h3>Assign Transport</h3>

      <input name="assignedDate" onChange={handleChange} placeholder="Date" />
      <input name="driverName" onChange={handleChange} placeholder="Driver Name" />
      <input name="contact" onChange={handleChange} placeholder="Contact" />
      <input name="vehicleNo" onChange={handleChange} placeholder="Vehicle No" />

      <button onClick={assign}>Assign</button>
    </div>
  );
};

export default AssignTransport;
