import React, { useState } from "react";
import { Link } from "react-router-dom";
import AddProduct from "./AddProduct";
import ProductList from "./ProductList";
import "./FarmerDashboard.css";

const FarmerDashboard = ({ user, logout }) => {
  const [refreshKey, setRefreshKey] = useState(0);

  // ðŸ”„ Called after a product is successfully added
  const handleProductAdded = () => {
    setRefreshKey((prev) => prev + 1);
  };

  return (
    <div>
      {/* Navbar */}
      <nav className="navbar">
        <div className="nav-brand">Product Tracking using Blockchain</div>
        <div className="nav-links">
          <Link to="/" className="nav-link">
            HOME
          </Link>
          <span style={{ color: "white" }}>Welcome, {user?.name}</span>
          <button onClick={logout} className="logout-btn">
            LOGOUT
          </button>
        </div>
      </nav>

      {/* Dashboard Section */}
      <div className="dashboard">
        <div className="dashboard-header">
          <h1>Farmer Dashboard</h1>
          <p>Manage your products and track your inventory</p>
        </div>

        {/* âœ… Add Product Form */}
        <div className="add-product-section">
          <AddProduct onProductAdded={handleProductAdded} />
        </div>

        {/* âœ… Product List (auto-refreshes when new product added) */}
        <div className="product-list-section">
          <h2>Your Products</h2>
          <ProductList key={refreshKey} />
        </div>
      </div>
    </div>
  );
};

export default FarmerDashboard;
