import React, { useState, useEffect } from "react";
import { productAPI, retailerAPI } from "../services/api";
import "./AddProduct.css";

const AddProduct = ({ onProductAdded }) => {
  const [retailers, setRetailers] = useState([]);

  const [formData, setFormData] = useState({
    name: "",
    category: "",
    price: "",
    batchNumber: "",
    productionDate: "",
    description: "",
    retailerId: "", // ⭐ REQUIRED
  });

  const [image, setImage] = useState(null);
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    retailerAPI
      .getAllRetailers()
      .then((res) => setRetailers(res.data))
      .catch((err) => console.error("Retailer load failed", err));
  }, []);

  const handleChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleImageChange = (e) => setImage(e.target.files[0]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");

    try {
      const farmer = JSON.parse(localStorage.getItem("user"));
      if (!farmer) {
        alert("⚠ Please log in before adding products!");
        setLoading(false);
        return;
      }

      if (!formData.retailerId) {
        alert("⚠ Please select a retailer!");
        setLoading(false);
        return;
      }

      const productData = {
        ...formData,
        price: parseFloat(formData.price),
        farmerEmail: farmer.email,
      };

      const form = new FormData();
      form.append(
        "product",
        new Blob([JSON.stringify(productData)], {
          type: "application/json",
        })
      );
      if (image) {
        form.append("image", image);
      }

      await productAPI.addProduct(form);

      setMessage("✅ Product added successfully!");

      setFormData({
        name: "",
        category: "",
        price: "",
        batchNumber: "",
        productionDate: "",
        description: "",
        retailerId: "",
      });
      setImage(null);
    } catch (error) {
      setMessage("❌ Failed to add product");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="add-product-container">
      <h2>➕ Add Product</h2>

      {/* Retailer Selection */}
      <div className="form-group">
        <label>Select Retailer:</label>
        <select
          name="retailerId"
          value={formData.retailerId}
          onChange={handleChange}
          required
        >
          <option value="">-- Select Retailer --</option>
          {retailers.map((r) => (
            <option key={r.id} value={r.id}>
              {r.storeName} ({r.ownerName})
            </option>
          ))}
        </select>
      </div>

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Name:</label>
          <input name="name" value={formData.name} onChange={handleChange} />
        </div>

        <div className="form-group">
          <label>Category:</label>
          <input name="category" value={formData.category} onChange={handleChange} />
        </div>

        <div className="form-group">
          <label>Price:</label>
          <input name="price" type="number" value={formData.price} onChange={handleChange} />
        </div>

        <div className="form-group">
          <label>Batch Number:</label>
          <input name="batchNumber" value={formData.batchNumber} onChange={handleChange} />
        </div>

        <div className="form-group">
          <label>Production Date:</label>
          <input name="productionDate" type="date" value={formData.productionDate} onChange={handleChange} />
        </div>

        <div className="form-group">
          <label>Image:</label>
          <input type="file" id="imageInput" onChange={handleImageChange} />
        </div>

        <div className="form-group">
          <label>Description:</label>
          <textarea name="description" value={formData.description} onChange={handleChange} />
        </div>

        <button type="submit" disabled={loading}>
          {loading ? "Adding..." : "Add Product"}
        </button>
      </form>

      {message && <p>{message}</p>}
    </div>
  );
};

export default AddProduct;
