import React, { useEffect, useState } from "react";
import { retailerAPI, productAPI } from "../services/api";

const RetailerDashboard = () => {
  const [farmersWithProducts, setFarmersWithProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchFarmersAndProducts = async () => {
      try {
        setLoading(true);
        const farmerResponse = await retailerAPI.getAllFarmers();
        const farmers = farmerResponse.data;

        // Fetch products for each farmer concurrently
        const farmersData = await Promise.all(
          farmers.map(async (farmer) => {
            try {
              const productResponse = await productAPI.getProductsByFarmer(farmer.id);
              return { ...farmer, products: productResponse.data };
            } catch (err) {
              console.error(`Error fetching products for farmer ${farmer.id}:`, err);
              return { ...farmer, products: [] };
            }
          })
        );

        setFarmersWithProducts(farmersData);
      } catch (err) {
        console.error("Error fetching farmers:", err);
        setError("Failed to load farmers. Please try again later.");
      } finally {
        setLoading(false);
      }
    };

    fetchFarmersAndProducts();
  }, []);

  if (loading) return <p>Loading farmers and products...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  return (
    <div className="dashboard-container" style={{ padding: "20px" }}>
      <h2>Retailer Dashboard</h2>
      <h3>Farmers and Their Products</h3>

      {farmersWithProducts.length === 0 ? (
        <p>No farmers found.</p>
      ) : (
        farmersWithProducts.map((farmer) => (
          <div
            key={farmer.id}
            style={{
              border: "1px solid #ccc",
              borderRadius: "8px",
              padding: "15px",
              marginBottom: "25px",
              backgroundColor: "#f9f9f9",
            }}
          >
            <h4>
              üë®‚Äçüåæ {farmer.name} ({farmer.email}) ‚Äî {farmer.phone}
            </h4>
            <p>
              <strong>Address:</strong> {farmer.address}
            </p>

            {farmer.products.length > 0 ? (
              <table
                border="1"
                cellPadding="8"
                style={{
                  width: "100%",
                  backgroundColor: "#fff",
                  borderCollapse: "collapse",
                }}
              >
                <thead style={{ backgroundColor: "#eaeaea" }}>
                  <tr>
                    <th>Product Name</th>
                    <th>Category</th>
                    <th>Price</th>
                    <th>Quantity</th>
                    <th>Description</th>
                  </tr>
                </thead>
                <tbody>
                  {farmer.products.map((p) => (
                    <tr key={p.id}>
                      <td>{p.name}</td>
                      <td>{p.category}</td>
                      <td>‚Çπ{p.price}</td>
                      <td>{p.quantity}</td>
                      <td>{p.description}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            ) : (
              <p style={{ color: "gray" }}>No products available for this farmer.</p>
            )}
          </div>
        ))
      )}
    </div>
  );
};

export default RetailerDashboard;
