import React, { useEffect, useState } from "react";
import { retailerAPI, cartAPI } from "../services/api";
import { useNavigate } from "react-router-dom";
import "./RetailerDashboard.css";

const RetailerDashboard = () => {
  const [farmers, setFarmers] = useState([]);
  const [quantities, setQuantities] = useState({});
  const [retailer, setRetailer] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const storedRetailer = JSON.parse(localStorage.getItem("retailer"));
    if (!storedRetailer) {
      alert("Please login as a retailer first!");
      navigate("/retailer/login");
      return;
    }
    setRetailer(storedRetailer);
  }, [navigate]);

  useEffect(() => {
    const fetchFarmers = async () => {
      try {
        const response = await retailerAPI.getAllFarmersWithProducts();
        setFarmers(response.data);
      } catch (error) {
        console.error("Error fetching farmers and products:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchFarmers();
  }, []);

  const handleQuantityChange = (productId, value) => {
    setQuantities((prev) => ({
      ...prev,
      [productId]: Math.max(1, Number(value)),
    }));
  };

  const handleAddToCart = async (productId) => {
    if (!retailer) return alert("Retailer not logged in!");
    const quantity = quantities[productId] || 1;

    try {
      await cartAPI.addToCart({
        retailerId: retailer.id,
        productId,
        quantity,
      });
      alert("‚úÖ Product added to cart!");
    } catch (error) {
      console.error("Error adding to cart:", error);
      alert("‚ùå Failed to add product to cart.");
    }
  };

  if (loading) return <p>Loading...</p>;

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h1>üõçÔ∏è Retailer Dashboard</h1>
        <p>Browse farmers and add products to your cart</p>
        <button
          className="view-cart-btn"
          onClick={() => navigate("/retailer/cart")}
        >
          üõí View Cart
        </button>
      </div>

      {farmers.map((farmer) => (
        <div key={farmer.id} className="store-profile">
          <h3>üë®‚Äçüåæ {farmer.name}</h3>
          <p><b>Email:</b> {farmer.email}</p>
          <p><b>Phone:</b> {farmer.phone}</p>
          <p><b>Address:</b> {farmer.address}</p>

          {farmer.products?.length > 0 ? (
            <table className="product-table">
              <thead>
                <tr>
                  <th>Product Name</th>
                  <th>Category</th>
                  <th>Price</th>
                  <th>Description</th>
                  <th>Image</th>
                  <th>Quantity</th>
                  <th>Add to Cart</th>
                </tr>
              </thead>
              <tbody>
                {farmer.products.map((p) => (
                  <tr key={p.id}>
                    <td>{p.name}</td>
                    <td>{p.category}</td>
                    <td>‚Çπ{p.price}</td>
                    <td>{p.description}</td>
                    <td>
                      <img
                        src={`http://localhost:8080/uploads/${p.imageName}`}
                        alt={p.name}
                        width="80"
                        height="80"
                        style={{ objectFit: "cover", borderRadius: "8px" }}
                      />
                    </td>
                    <td>
                      <input
                        type="number"
                        min="1"
                        value={quantities[p.id] || 1}
                        onChange={(e) =>
                          handleQuantityChange(p.id, e.target.value)
                        }
                        style={{ width: "60px", textAlign: "center" }}
                      />
                    </td>
                    <td>
                      <button
                        className="add-cart-btn"
                        onClick={() => handleAddToCart(p.id)}
                      >
                        üõí Add
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            <p style={{ color: "gray" }}>No products available.</p>
          )}
        </div>
      ))}
    </div>
  );
};

export default RetailerDashboard;
