import React, { useState } from "react";
import { productAPI } from "../services/api";
import "./AddProduct.css";

const AddProduct = ({ onProductAdded }) => {
  const [formData, setFormData] = useState({
    name: "",
    category: "",
    price: "",
    batchNumber: "",
    productionDate: "",
    description: "",
  });
  const [image, setImage] = useState(null);
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleImageChange = (e) => {
    setImage(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");

    try {
      const submitData = new FormData();
      const productData = {
        ...formData,
        price: parseFloat(formData.price),
        productionDate: formData.productionDate || null,
      };

      submitData.append(
        "product",
        new Blob([JSON.stringify(productData)], { type: "application/json" })
      );
      if (image) submitData.append("image", image);

      await productAPI.addProduct(submitData);

      setMessage("✅ Product added successfully!");
      setFormData({
        name: "",
        category: "",
        price: "",
        batchNumber: "",
        productionDate: "",
        description: "",
      });
      setImage(null);
      document.getElementById("imageInput").value = "";

      // ✅ Notify parent dashboard to refresh product list
      if (onProductAdded) onProductAdded();
    } catch (error) {
      console.error("Error adding product:", error);
      if (error.response?.data) {
        const data = error.response.data;
        const errorMessage =
          typeof data === "string"
            ? data
            : data.error || JSON.stringify(data);
        setMessage("❌ Error adding product: " + errorMessage);
      } else {
        setMessage("❌ Network or unknown error: " + error.message);
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="add-product-container">
      <h2>Add New Product</h2>

      {message && (
        <div
          className={`alert ${
            message.includes("Error") || message.includes("❌")
              ? "alert-error"
              : "alert-success"
          }`}
        >
          {message}
        </div>
      )}

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Product Name:</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Category:</label>
          <input
            type="text"
            name="category"
            value={formData.category}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Price (₹):</label>
          <input
            type="number"
            step="0.01"
            name="price"
            value={formData.price}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Batch Number:</label>
          <input
            type="text"
            name="batchNumber"
            value={formData.batchNumber}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Production Date:</label>
          <input
            type="datetime-local"
            name="productionDate"
            value={formData.productionDate}
            onChange={handleChange}
          />
        </div>

        <div className="form-group">
          <label>Product Image:</label>
          <input
            id="imageInput"
            type="file"
            accept="image/*"
            onChange={handleImageChange}
          />
        </div>

        <div className="form-group">
          <label>Description:</label>
          <textarea
            name="description"
            value={formData.description}
            onChange={handleChange}
            rows="3"
          />
        </div>

        <button type="submit" className="submit-btn" disabled={loading}>
          {loading ? "Adding Product..." : "Add Product"}
        </button>
      </form>
    </div>
  );
};

export default AddProduct;
