import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './ProductManagement.css';

const ProductManagement = () => {
    const [products, setProducts] = useState([]);
    const [showAddForm, setShowAddForm] = useState(false);
    const [loading, setLoading] = useState(false);
    const [formData, setFormData] = useState({
        productName: '',
        productType: '',
        description: '',
        plantingDate: '',
        expectedHarvestDate: '',
        weight: '',
        unit: 'kg',
        qualityGrade: 'A'
    });

    useEffect(() => {
        fetchProducts();
    }, []);

    const fetchProducts = async () => {
        try {
            const response = await fetch('http://localhost:8080/api/products/my-products', {
                credentials: 'include'
            });

            if (response.ok) {
                const data = await response.json();
                setProducts(data);
            }
        } catch (error) {
            console.error('Error fetching products:', error);
        }
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);

        try {
            const response = await fetch('http://localhost:8080/api/products/add', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formData),
                credentials: 'include'
            });

            if (response.ok) {
                const newProduct = await response.json();
                setProducts(prev => [newProduct, ...prev]);
                setFormData({
                    productName: '',
                    productType: '',
                    description: '',
                    plantingDate: '',
                    expectedHarvestDate: '',
                    weight: '',
                    unit: 'kg',
                    qualityGrade: 'A'
                });
                setShowAddForm(false);
                alert('Product added successfully!');
            } else {
                const errorText = await response.text();
                alert('Error adding product: ' + errorText);
            }
        } catch (error) {
            console.error('Error adding product:', error);
            alert('Error adding product');
        } finally {
            setLoading(false);
        }
    };

    const markAsHarvested = async (productId) => {
        try {
            const response = await fetch(`http://localhost:8080/api/products/${productId}/harvest`, {
                method: 'POST',
                credentials: 'include'
            });

            if (response.ok) {
                fetchProducts(); // Refresh the list
                alert('Product marked as harvested!');
            }
        } catch (error) {
            console.error('Error marking as harvested:', error);
        }
    };

    const formatDate = (dateString) => {
        if (!dateString) return 'Not set';
        return new Date(dateString).toLocaleDateString();
    };

    return (
        <div className="product-management">
            <div className="page-header">
                <h1>My Products</h1>
                <button 
                    className="btn-primary"
                    onClick={() => setShowAddForm(true)}
                >
                    Add New Product
                </button>
            </div>

            {showAddForm && (
                <div className="modal-overlay">
                    <div className="modal">
                        <div className="modal-header">
                            <h2>Add New Product</h2>
                            <button 
                                className="close-btn"
                                onClick={() => setShowAddForm(false)}
                            >
                                Ã—
                            </button>
                        </div>
                        <form onSubmit={handleSubmit} className="product-form">
                            <div className="form-group">
                                <label>Product Name *</label>
                                <input
                                    type="text"
                                    name="productName"
                                    value={formData.productName}
                                    onChange={handleInputChange}
                                    required
                                    placeholder="e.g., Organic Tomatoes"
                                />
                            </div>

                            <div className="form-group">
                                <label>Product Type *</label>
                                <select
                                    name="productType"
                                    value={formData.productType}
                                    onChange={handleInputChange}
                                    required
                                >
                                    <option value="">Select Type</option>
                                    <option value="VEGETABLE">Vegetable</option>
                                    <option value="FRUIT">Fruit</option>
                                    <option value="GRAIN">Grain</option>
                                    <option value="DAIRY">Dairy</option>
                                    <option value="POULTRY">Poultry</option>
                                    <option value="LIVESTOCK">Livestock</option>
                                    <option value="OTHER">Other</option>
                                </select>
                            </div>

                            <div className="form-group">
                                <label>Description</label>
                                <textarea
                                    name="description"
                                    value={formData.description}
                                    onChange={handleInputChange}
                                    rows="3"
                                    placeholder="Product description, variety, etc."
                                />
                            </div>

                            <div className="form-row">
                                <div className="form-group">
                                    <label>Planting Date</label>
                                    <input
                                        type="date"
                                        name="plantingDate"
                                        value={formData.plantingDate}
                                        onChange={handleInputChange}
                                    />
                                </div>

                                <div className="form-group">
                                    <label>Expected Harvest Date</label>
                                    <input
                                        type="date"
                                        name="expectedHarvestDate"
                                        value={formData.expectedHarvestDate}
                                        onChange={handleInputChange}
                                    />
                                </div>
                            </div>

                            <div className="form-row">
                                <div className="form-group">
                                    <label>Weight</label>
                                    <input
                                        type="number"
                                        name="weight"
                                        value={formData.weight}
                                        onChange={handleInputChange}
                                        step="0.01"
                                        placeholder="0.00"
                                    />
                                </div>

                                <div className="form-group">
                                    <label>Unit</label>
                                    <select
                                        name="unit"
                                        value={formData.unit}
                                        onChange={handleInputChange}
                                    >
                                        <option value="kg">kg</option>
                                        <option value="g">g</option>
                                        <option value="lb">lb</option>
                                        <option value="ton">ton</option>
                                        <option value="piece">piece</option>
                                    </select>
                                </div>

                                <div className="form-group">
                                    <label>Quality Grade</label>
                                    <select
                                        name="qualityGrade"
                                        value={formData.qualityGrade}
                                        onChange={handleInputChange}
                                    >
                                        <option value="A">Grade A</option>
                                        <option value="B">Grade B</option>
                                        <option value="C">Grade C</option>
                                        <option value="ORGANIC">Organic</option>
                                    </select>
                                </div>
                            </div>

                            <div className="form-actions">
                                <button
                                    type="button"
                                    className="btn-secondary"
                                    onClick={() => setShowAddForm(false)}
                                >
                                    Cancel
                                </button>
                                <button
                                    type="submit"
                                    className="btn-primary"
                                    disabled={loading}
                                >
                                    {loading ? 'Adding...' : 'Add Product'}
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            )}

            <div className="products-list">
                {products.length === 0 ? (
                    <div className="empty-state">
                        <h3>No products yet</h3>
                        <p>Start by adding your first farm product to track on blockchain.</p>
                        <button 
                            className="btn-primary"
                            onClick={() => setShowAddForm(true)}
                        >
                            Add First Product
                        </button>
                    </div>
                ) : (
                    <div className="products-grid">
                        {products.map(product => (
                            <div key={product.productId} className="product-card">
                                <div className="product-header">
                                    <h3>{product.productName}</h3>
                                    <span className={`product-type-badge ${product.productType?.toLowerCase()}`}>
                                        {product.productType}
                                    </span>
                                </div>

                                {product.description && (
                                    <p className="product-description">{product.description}</p>
                                )}

                                <div className="product-details">
                                    <div className="detail-item">
                                        <strong>Product ID:</strong> 
                                        <span className="product-id">{product.productId}</span>
                                    </div>

                                    {product.plantingDate && (
                                        <div className="detail-item">
                                            <strong>Planted:</strong> 
                                            <span>{formatDate(product.plantingDate)}</span>
                                        </div>
                                    )}

                                    {product.expectedHarvestDate && (
                                        <div className="detail-item">
                                            <strong>Expected Harvest:</strong> 
                                            <span>{formatDate(product.expectedHarvestDate)}</span>
                                        </div>
                                    )}

                                    {product.harvestDate && (
                                        <div className="detail-item harvested">
                                            <strong>Harvested:</strong> 
                                            <span>{formatDate(product.harvestDate)}</span>
                                        </div>
                                    )}

                                    {product.weight && (
                                        <div className="detail-item">
                                            <strong>Weight:</strong> 
                                            <span>{product.weight} {product.unit}</span>
                                        </div>
                                    )}

                                    {product.qualityGrade && (
                                        <div className="detail-item">
                                            <strong>Quality:</strong> 
                                            <span className={`quality-${product.qualityGrade.toLowerCase()}`}>
                                                {product.qualityGrade}
                                            </span>
                                        </div>
                                    )}

                                    {product.blockchainHash && (
                                        <div className="detail-item">
                                            <strong>Blockchain ID:</strong> 
                                            <span className="blockchain-id">
                                                {product.blockchainHash.substring(0, 20)}...
                                            </span>
                                        </div>
                                    )}
                                </div>

                                <div className="product-actions">
                                    <Link 
                                        to={`/products/${product.productId}`}
                                        className="btn-outline"
                                    >
                                        View Details
                                    </Link>
                                    {!product.harvestDate && (
                                        <button 
                                            className="btn-harvest"
                                            onClick={() => markAsHarvested(product.productId)}
                                        >
                                            Mark Harvested
                                        </button>
                                    )}
                                </div>
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
};

export default ProductManagement;
