import React, { useEffect, useState } from "react";
import { retailerAPI, cartAPI } from "../services/api";
import { useParams, useNavigate } from "react-router-dom";
import "./RetailerFarmersProducts.css";

const RetailerFarmerProducts = () => {
  const { farmerId } = useParams();
  const navigate = useNavigate();

  const [retailer, setRetailer] = useState(null);
  const [farmer, setFarmer] = useState(null);
  const [products, setProducts] = useState([]);
  const [quantities, setQuantities] = useState({});

  // ------------------------------------------------
  // AUTH CHECK
  // ------------------------------------------------
  useEffect(() => {
    try {
      const rawUser = localStorage.getItem("user");
      const userType = localStorage.getItem("userType");

      if (!rawUser || userType !== "retailer") {
        navigate("/retailer/login");
        return;
      }

      const parsed = JSON.parse(rawUser);
      setRetailer(parsed);

      loadFarmer(farmerId);

    } catch (err) {
      console.error("Auth error:", err);
      navigate("/retailer/login");
    }
  }, [farmerId, navigate]);

  // ------------------------------------------------
  // LOAD FARMER + PRODUCTS
  // ------------------------------------------------
  const loadFarmer = async (id) => {
    try {
      const res = await retailerAPI.getFarmerById(id);
      setFarmer(res.data);
      setProducts(res.data.products || []);
    } catch (error) {
      console.error("Error loading farmer:", error);
    }
  };

  // ------------------------------------------------
  // ADD TO CART (with quantity)
  // ------------------------------------------------
  const addToCart = async (productId) => {
    const quantity = quantities[productId] || 1;

    try {
      await cartAPI.addToCart(retailer.id, productId, quantity);

      alert(`‚úî Added ${quantity} item(s) to cart!`);
      navigate("/retailer/cart");

    } catch (err) {
      console.error("Error adding to cart:", err);
      alert("‚ùå Failed to add item to cart");
    }
  };

  // ------------------------------------------------
  // UI
  // ------------------------------------------------
  return (
    <div className="farmer-products-page">

      {/* HEADER */}
      <div className="header-bar">
        <button className="back-btn" onClick={() => navigate("/retailer/dashboard")}>
          ‚Üê Back
        </button>

        <h2>üßë‚Äçüåæ Farmer Products</h2>

        <button className="cart-btn" onClick={() => navigate("/retailer/cart")}>
          üõí Cart
        </button>
      </div>

      {!farmer ? (
        <p className="loading">Loading farmer...</p>
      ) : (
        <>
          {/* FARMER INFO */}
          <div className="farmer-info">
            <h3>{farmer.name}</h3>
            <p><b>üìû Phone:</b> {farmer.phone}</p>
            <p><b>üìç Address:</b> {farmer.address}</p>
            <p><b>üß∫ Total Products:</b> {products.length}</p>
          </div>

          {/* PRODUCT LIST */}
          {products.length === 0 ? (
            <p>No products available for this farmer.</p>
          ) : (
            <table className="product-table">
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Category</th>
                  <th>Price (‚Çπ)</th>
                  <th>Qty</th>
                  <th>Add</th>
                </tr>
              </thead>

              <tbody>
                {products.map((p) => (
                  <tr key={p.id}>
                    <td>{p.name}</td>
                    <td>{p.category}</td>
                    <td>‚Çπ{Number(p.price || 0).toFixed(2)}</td>

                    {/* Quantity Input */}
                    <td>
                      <input
                        type="number"
                        min="1"
                        className="qty-input"
                        value={quantities[p.id] || 1}
                        onChange={(e) =>
                          setQuantities({
                            ...quantities,
                            [p.id]: Number(e.target.value),
                          })
                        }
                      />
                    </td>

                    <td>
                      <button className="add-btn" onClick={() => addToCart(p.id)}>
                        ‚ûï Add
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </>
      )}
    </div>
  );
};

export default RetailerFarmerProducts;
