import React from "react";
import { Link, useNavigate } from "react-router-dom";


const RetailerHeader = ({ retailer }) => {
  const navigate = useNavigate();

  const handleLogout = () => {
    if (window.confirm("Are you sure you want to logout?")) {
      localStorage.removeItem("retailer");
      navigate("/retailer/login", { replace: true });
    }
  };

  return (
    <header className="retailer-header">
      <div className="brand">üåæ Product Tracker</div>
      <nav className="nav-links">
        <Link to="/retailer/dashboard" className="nav-link">
          üè† Dashboard
        </Link>
        <Link to="/retailer/orders" className="nav-link">
          üì¶ My Orders
        </Link>
        <Link to="/retailer/cart" className="nav-link">
          üõçÔ∏è Cart
        </Link>
        {retailer && (
          <span className="welcome-text">
            Welcome, <b>{retailer.storeName || retailer.name}</b>
          </span>
        )}
        <button className="logout-btn" onClick={handleLogout}>
          üö™ Logout
        </button>
      </nav>
    </header>
  );
};

export default RetailerHeader;
