import React, { useEffect, useState } from "react";
import { transportAPI } from "../services/api";

const TransportDashboard = () => {
  const transporter = JSON.parse(localStorage.getItem("transporter"));
  const transporterId = transporter?.id;

  const [deliveries, setDeliveries] = useState([]);

  useEffect(() => {
    if (transporterId) {
      transportAPI.getTransporterDeliveries(transporterId)
        .then((res) => setDeliveries(res.data));
    }
  }, [transporterId]);

  const updateStatus = (transportId, status) => {
    transportAPI.updateDeliveryStatus(transportId, status)
      .then(() => {
        alert("Status updated");
        window.location.reload();
      });
  };

  return (
    <div>
      <h1>ðŸšš Transporter Dashboard</h1>

      {deliveries.length === 0 ? (
        <p>No deliveries assigned.</p>
      ) : (
        deliveries.map((d) => (
          <div key={d.id} className="delivery-card">
            <p><b>Order:</b> {d.order.id}</p>
            <p><b>Farmer:</b> {d.order.farmer.name}</p>
            <p><b>Status:</b> {d.status}</p>

            <button onClick={() => updateStatus(d.id, "PICKED_UP")}>
              Mark Picked Up
            </button>

            <button onClick={() => updateStatus(d.id, "DELIVERED")}>
              Mark Delivered
            </button>
          </div>
        ))
      )}
    </div>
  );
};

export default TransportDashboard;
