import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import { transporterAPI } from "../services/api";
import "./TransportSignup.css";

const TransportSignup = () => {
  const [form, setForm] = useState({
    name: "",
    email: "",
    password: "",
    phone: "",
    vehicleNo: "",
  });

  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      await transporterAPI.signup(form);
      alert("‚úÖ Registration successful!");
      navigate("/transport/login");
    } catch (err) {
      alert("‚ùå Signup failed");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      {/* ‚≠ê Header added here */}
      <Header />

      <div className="auth-container">
        <h1>üöõ Transporter Registration</h1>

        <form onSubmit={handleSubmit}>
          {["name", "email", "password", "phone", "vehicleNo"].map((f) => (
            <input
              key={f}
              name={f}
              type={f === "password" ? "password" : "text"}
              placeholder={f.charAt(0).toUpperCase() + f.slice(1)}
              value={form[f]}
              onChange={handleChange}
              required
            />
          ))}

          <button type="submit" disabled={loading}>
            {loading ? "‚è≥ Registering..." : "Register"}
          </button>
        </form>

        <p>
          Already registered?{" "}
          <Link to="/transport/login">Login here</Link>
        </p>
      </div>
    </>
  );
};

export default TransportSignup;
