import React, { useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { transportAPI } from "../services/api";

const FarmerAssignTransport = () => {
  const { orderId } = useParams();
  const navigate = useNavigate();

  const [form, setForm] = useState({
    vehicleNo: "",
    driverName: "",
    contact: "",
    transporter: { id: "" }
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await transportAPI.assignTransport(orderId, form);
      alert("Transport assigned successfully!");
      navigate("/farmer/orders");   // ✔ correct redirect
    } catch (err) {
      console.error(err);
      alert("Failed to assign transport");
    }
  };

  return (
    <div className="assign-box">
      <h2>Assign Transport for Order #{orderId}</h2>

      <form onSubmit={handleSubmit}>
        <input
          placeholder="Vehicle Number"
          value={form.vehicleNo}
          onChange={(e) => setForm({ ...form, vehicleNo: e.target.value })}
          required
        />

        <input
          placeholder="Driver Name"
          value={form.driverName}
          onChange={(e) => setForm({ ...form, driverName: e.target.value })}
          required
        />

        <input
          placeholder="Driver Contact"
          value={form.contact}
          onChange={(e) => setForm({ ...form, contact: e.target.value })}
          required
        />

        <input
          placeholder="Transporter ID"
          value={form.transporter.id}
          onChange={(e) => setForm({ ...form, transporter: { id: e.target.value } })}
          required
        />

        <button type="submit">Assign Transport</button>
      </form>
    </div>
  );
};

export default FarmerAssignTransport;
