import React, { useEffect, useState, useCallback } from "react";
import { cartAPI, orderAPI } from "../services/api";
import "./RetailerCart.css";

const RetailerCart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [retailer, setRetailer] = useState(null);
  const [total, setTotal] = useState(0);
  const [loading, setLoading] = useState(true);

  // ‚úÖ Stable fetchCart using useCallback
  const fetchCart = useCallback(async (retailerId) => {
    try {
      const res = await cartAPI.getCart(retailerId);
      setCartItems(res.data);
      calculateTotal(res.data);
    } catch (err) {
      console.error("Error fetching cart:", err);
    } finally {
      setLoading(false);
    }
  }, []);

  const calculateTotal = (items) => {
    const totalPrice = items.reduce(
      (sum, item) => sum + item.product.price * item.quantity,
      0
    );
    setTotal(totalPrice);
  };

  useEffect(() => {
    const stored = JSON.parse(localStorage.getItem("retailer"));
    if (!stored) {
      alert("Please log in to view your cart.");
      window.location.href = "/retailer/login";
      return;
    }
    setRetailer(stored);
    fetchCart(stored.id);
  }, [fetchCart]); // ‚úÖ Now fetchCart is safely included

  const handleRemove = async (itemId) => {
    if (!window.confirm("üóëÔ∏è Remove this item from your cart?")) return;

    try {
      await cartAPI.removeItem(itemId);
      const updated = cartItems.filter((i) => i.id !== itemId);
      setCartItems(updated);
      calculateTotal(updated);
    } catch (err) {
      console.error("Error removing item:", err);
      alert("‚ùå Failed to remove item.");
    }
  };

  const handleClearCart = async () => {
    if (!retailer) return;
    if (!window.confirm("‚ö†Ô∏è Are you sure you want to clear your entire cart?")) return;

    try {
      await cartAPI.clearCart(retailer.id);
      setCartItems([]);
      setTotal(0);
      alert("üßπ Cart cleared successfully!");
    } catch (err) {
      console.error("Error clearing cart:", err);
      alert("‚ùå Failed to clear cart.");
    }
  };

  const handlePlaceOrder = async () => {
    if (!window.confirm("‚úÖ Confirm order placement?")) return;
    try {
      await orderAPI.placeOrder(retailer.id);
      alert("üéâ Order placed successfully!");
      setCartItems([]);
      setTotal(0);
    } catch (err) {
      console.error("Error placing order:", err);
      alert("‚ùå Failed to place order. Try again.");
    }
  };

  if (loading) return <p className="loading">Loading your cart...</p>;

  return (
    <div className="cart-page">
      <div className="cart-header">
        <h1>üõí Your Shopping Cart</h1>
        {retailer && <p>Welcome, <b>{retailer.storeName || retailer.name}</b></p>}
      </div>

      {cartItems.length === 0 ? (
        <div className="empty-cart">
          <h2>üõçÔ∏è Your cart is empty</h2>
          <p>Browse products and add them to your cart to continue shopping.</p>
          <a href="/retailer/dashboard" className="back-btn">
            ‚Üê Back to Dashboard
          </a>
        </div>
      ) : (
        <>
          <table className="cart-table">
            <thead>
              <tr>
                <th>Product</th>
                <th>Quantity</th>
                <th>Price (each)</th>
                <th>Total</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {cartItems.map((item) => (
                <tr key={item.id}>
                  <td>{item.product.name}</td>
                  <td>{item.quantity}</td>
                  <td>‚Çπ{item.product.price}</td>
                  <td>‚Çπ{(item.product.price * item.quantity).toFixed(2)}</td>
                  <td>
                    <button
                      onClick={() => handleRemove(item.id)}
                      className="remove-btn"
                    >
                      ‚ùå Remove
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          <div className="cart-summary">
            <div className="summary-details">
              <h3>Total Amount:</h3>
              <h2 className="price-tag">‚Çπ{total.toFixed(2)}</h2>
            </div>
            <div className="summary-actions">
              <button onClick={handlePlaceOrder} className="place-btn">
                ‚úÖ Place Order
              </button>
              <button onClick={handleClearCart} className="clear-btn">
                üßπ Clear Cart
              </button>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default RetailerCart;
