import React, { useEffect, useState } from "react";
import { cartAPI } from "../services/api";
import "./RetailerCart.css";

const RetailerCart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [retailer, setRetailer] = useState(null);

  useEffect(() => {
    const stored = JSON.parse(localStorage.getItem("retailer"));
    setRetailer(stored);
    if (stored) fetchCart(stored.id);
  }, []);

  const fetchCart = async (retailerId) => {
    try {
      const res = await cartAPI.getCart(retailerId);
      setCartItems(res.data);
    } catch (err) {
      console.error("Error fetching cart:", err);
    }
  };

  const handleRemove = async (itemId) => {
    try {
      await cartAPI.removeItem(itemId);
      setCartItems((prev) => prev.filter((i) => i.id !== itemId));
    } catch (err) {
      console.error("Error removing item:", err);
    }
  };

  const handleClearCart = async () => {
    if (!retailer) return;
    try {
      await cartAPI.clearCart(retailer.id);
      setCartItems([]);
    } catch (err) {
      console.error("Error clearing cart:", err);
    }
  };

  return (
    <div className="cart-page">
      <h1>üõí Your Cart</h1>
      {cartItems.length === 0 ? (
        <p>No items in your cart.</p>
      ) : (
        <>
          <table className="cart-table">
            <thead>
              <tr>
                <th>Product</th>
                <th>Quantity</th>
                <th>Price (each)</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {cartItems.map((item) => (
                <tr key={item.id}>
                  <td>{item.product.name}</td>
                  <td>{item.quantity}</td>
                  <td>‚Çπ{item.product.price}</td>
                  <td>
                    <button
                      onClick={() => handleRemove(item.id)}
                      className="remove-btn"
                    >
                      ‚ùå Remove
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          <button onClick={handleClearCart} className="clear-btn">
            üßπ Clear Cart
          </button>
        </>
      )}
    </div>
  );
};

export default RetailerCart;
