// src/components/PaymentPage.js
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { orderAPI } from "../services/api";
import "./PaymentPage.css";

const PaymentPage = () => {
  const [method, setMethod] = useState("UPI");
  const [loading, setLoading] = useState(false);

  // read retailer (same key used in your cart)
  const retailer = JSON.parse(localStorage.getItem("retailer"));

  const navigate = useNavigate();

  const handlePayment = async () => {
    if (!retailer || !retailer.id) {
      alert("Retailer not found! Please login again.");
      return;
    }

    setLoading(true);
    try {
      const res = await orderAPI.placeOrder(retailer.id, method);
      // prefer server message if available
      alert(res?.data?.message || "‚úÖ Order placed successfully!");
      navigate("/retailer/orders");
    } catch (err) {
      console.error("Payment failed:", err);
      // show backend message if present
      const msg = err?.response?.data?.message || err?.message || "Failed to place order.";
      alert(`‚ùå ${msg}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="payment-container">
      <h2>üí≥ Choose Payment Method</h2>

      <div className="payment-options">
        <label>
          <input
            type="radio"
            value="UPI"
            checked={method === "UPI"}
            onChange={(e) => setMethod(e.target.value)}
          />
          UPI
        </label>

        <label>
          <input
            type="radio"
            value="CASH"
            checked={method === "CASH"}
            onChange={(e) => setMethod(e.target.value)}
          />
          Cash on Delivery
        </label>
      </div>

      <button className="pay-btn" onClick={handlePayment} disabled={loading}>
        {loading ? "Processing..." : "‚úÖ Confirm & Place Order"}
      </button>
    </div>
  );
};

export default PaymentPage;
