import React, { useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { transportAPI } from "../services/api";

const TransportDetails = () => {
  const { orderId } = useParams();
  const navigate = useNavigate();
  const [form, setForm] = useState({
    vehicleNo: "",
    driverName: "",
    contact: "",
  });

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await transportAPI.assignTransport(orderId, form);
      alert("‚úÖ Transport assigned successfully!");
      navigate("/transport/dashboard");
    } catch (err) {
      console.error("‚ùå Transport assignment failed:", err);
      alert("Assignment failed. Please check inputs or try again later.");
    }
  };

  return (
    <div className="transport-container">
      <h2>üöõ Assign Transport for Order #{orderId}</h2>
      <form onSubmit={handleSubmit}>
        {["vehicleNo", "driverName", "contact"].map((field) => (
          <div key={field}>
            <label>
              {field === "vehicleNo"
                ? "Vehicle Number"
                : field === "driverName"
                ? "Driver Name"
                : "Contact"}
            </label>
            <input
              name={field}
              placeholder={
                field === "vehicleNo"
                  ? "MH-12-AB-1234"
                  : field === "contact"
                  ? "+91 9876543210"
                  : "Enter driver name"
              }
              onChange={handleChange}
              required
            />
          </div>
        ))}
        <button type="submit">Assign Transport</button>
      </form>
    </div>
  );
};

export default TransportDetails;
