import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { transportAPI, transporterAPI } from "../services/api";



const FarmerAssignTransport = () => {
  const { orderId } = useParams();
  const navigate = useNavigate();

  const [form, setForm] = useState({
    vehicleNo: "",
    driverName: "",
    contact: "",
    transporter: { id: "" },
  });
  const [transporters, setTransporters] = useState([]);

  // Load all registered transporters
  useEffect(() => {
    transporterAuthAPI
      .getAll()
      .then((res) => setTransporters(res.data))
      .catch((err) => console.error("Error fetching transporters:", err));
  }, []);

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleTransporterChange = (e) =>
    setForm({ ...form, transporter: { id: e.target.value } });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await transportAPI.assignTransport(orderId, form);
      alert("‚úÖ Transport assigned successfully!");
      navigate("/farmer/dashboard");
    } catch (err) {
      console.error(err);
      alert("‚ùå Failed to assign transport.");
    }
  };

  return (
    <div className="assign-transport">
      <h2>üöõ Assign Transport for Order #{orderId}</h2>

      <form onSubmit={handleSubmit}>
        <label>Transporter</label>
        <select onChange={handleTransporterChange} required>
          <option value="">Select Transporter</option>
          {transporters.map((t) => (
            <option key={t.id} value={t.id}>
              {t.name} ({t.vehicleNo})
            </option>
          ))}
        </select>

        <input
          name="vehicleNo"
          placeholder="Vehicle Number"
          onChange={handleChange}
          required
        />
        <input
          name="driverName"
          placeholder="Driver Name"
          onChange={handleChange}
          required
        />
        <input
          name="contact"
          placeholder="Contact Number"
          onChange={handleChange}
          required
        />

        <button type="submit">Assign Transport</button>
      </form>
    </div>
  );
};

export default FarmerAssignTransport;
