import React, { useState, useEffect } from "react";
import { productAPI } from "../services/api";
import "./ProductList.css";

const ProductList = ({ refreshKey }) => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  const fetchProducts = async () => {
    setLoading(true);
    setError("");

    try {
      const farmer = JSON.parse(localStorage.getItem("user"));

      if (!farmer) {
        alert("Please log in as a farmer!");
        setLoading(false);
        return;
      }

      const response = await productAPI.getMyProducts(farmer.email);
      setProducts(response.data);

    } catch (err) {
      setError("Failed to load products.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, [refreshKey]);

  const getImageUrl = (imagePath) =>
    imagePath ? `http://localhost:8080/uploads/${imagePath}` : null;

  const getQrUrl = (qrPath) =>
    qrPath ? `http://localhost:8080${qrPath}` : null;

  if (loading) return <div>Loading your products...</div>;
  if (error) return <div style={{ color: "red" }}>‚ùå {error}</div>;

  return (
    <div className="product-list-container">
      <h2>üß∫ My Products</h2>

      <div className="products-grid">
        {products.map((product) => {
          const imageUrl = getImageUrl(product.imagePath);
          const qrUrl = getQrUrl(product.qrCodePath);

          return (
            <div key={product.id} className="product-card">

              {/* Product Image */}
              {imageUrl ? (
                <img src={imageUrl} className="product-image" />
              ) : (
                <div>No Image</div>
              )}

              {/* QR Code */}
              {qrUrl ? (
                <img src={qrUrl} className="qr-image" />
              ) : (
                <div>No QR</div>
              )}

              <h3>{product.name}</h3>
              <p>Category: {product.category}</p>
              <p>Price: ‚Çπ{product.price}</p>
              <p>Batch: {product.batchNumber}</p>
              <p>Production: {product.productionDate}</p>
              <p>{product.description}</p>

            </div>
          );
        })}
      </div>
    </div>
  );
};

export default ProductList;
