import React, { useState } from "react";
import { addProduct } from "../services/productService";
import { Spinner } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";

const AddProduct = () => {
  const [formData, setFormData] = useState({
    name: "",
    category: "",
    price: "",
    batchNumber: "",
    productionDate: "",
    description: "",
  });
  const [imageFile, setImageFile] = useState(null);
  const [status, setStatus] = useState({ message: "", type: "" });
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleFileChange = (e) => {
    setImageFile(e.target.files[0]);
  };

  const resetForm = () => {
    setFormData({
      name: "",
      category: "",
      price: "",
      batchNumber: "",
      productionDate: "",
      description: "",
    });
    setImageFile(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!imageFile) {
      setStatus({ message: "⚠️ Please select an image file", type: "error" });
      return;
    }

    setLoading(true);
    setStatus({ message: "", type: "" });

    try {
      const productData = {
        ...formData,
        price: parseFloat(formData.price),
        productionDate: formData.productionDate
          ? new Date(formData.productionDate).toISOString()
          : null,
      };

      await addProduct(productData, imageFile);
      setStatus({ message: "✅ Product added successfully!", type: "success" });
      resetForm();
    } catch (error) {
      const errMsg =
        error.response?.data?.message ||
        error.response?.data ||
        error.message ||
        "Unknown error";
      setStatus({ message: `❌ Error adding product: ${errMsg}`, type: "error" });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mt-5 p-4 rounded shadow-sm bg-light" style={{ maxWidth: "600px" }}>
      <h2 className="text-center mb-4">Add New Product</h2>

      {status.message && (
        <div
          className={`alert ${
            status.type === "success" ? "alert-success" : "alert-danger"
          }`}
          role="alert"
        >
          {status.message}
        </div>
      )}

      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label className="form-label fw-semibold">Product Name</label>
          <input
            type="text"
            name="name"
            className="form-control"
            placeholder="Enter product name"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </div>

        <div className="mb-3">
          <label className="form-label fw-semibold">Category</label>
          <input
            type="text"
            name="category"
            className="form-control"
            placeholder="e.g. Fruits, Vegetables, Dairy"
            value={formData.category}
            onChange={handleChange}
            required
          />
        </div>

        <div className="mb-3">
          <label className="form-label fw-semibold">Price (₹)</label>
          <input
            type="number"
            name="price"
            step="0.01"
            min="0"
            className="form-control"
            placeholder="Enter product price"
            value={formData.price}
            onChange={handleChange}
            required
          />
        </div>

        <div className="mb-3">
          <label className="form-label fw-semibold">Batch Number</label>
          <input
            type="text"
            name="batchNumber"
            className="form-control"
            placeholder="Enter batch number"
            value={formData.batchNumber}
            onChange={handleChange}
            required
          />
        </div>

        <div className="mb-3">
          <label className="form-label fw-semibold">Production Date</label>
          <input
            type="datetime-local"
            name="productionDate"
            className="form-control"
            value={formData.productionDate}
            onChange={handleChange}
          />
        </div>

        <div className="mb-4">
          <label className="form-label fw-semibold">Description</label>
          <textarea
            name="description"
            className="form-control"
            placeholder="Write a short product description..."
            rows="3"
            value={formData.description}
            onChange={handleChange}
          ></textarea>
        </div>

        <div className="mb-4">
          <label className="form-label fw-semibold">Product Image</label>
          <input
            type="file"
            className="form-control"
            accept="image/*"
            onChange={handleFileChange}
            required
          />
        </div>

        <div className="d-grid">
          <button type="submit" className="btn btn-primary fw-semibold" disabled={loading}>
            {loading ? (
              <>
                <Spinner
                  as="span"
                  animation="border"
                  size="sm"
                  role="status"
                  aria-hidden="true"
                  className="me-2"
                />
                Adding Product...
              </>
            ) : (
              "Add Product"
            )}
          </button>
        </div>
      </form>
    </div>
  );
};

export default AddProduct;
