import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "./RetailerHeader.css";

const RetailerHeader = () => {
  const [retailer, setRetailer] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const storedRetailer = JSON.parse(localStorage.getItem("retailer"));
    if (storedRetailer) {
      setRetailer(storedRetailer);
    } else {
      navigate("/retailer/login");
    }
  }, [navigate]);

  const handleLogout = () => {
    localStorage.clear();
    navigate("/retailer/login");
  };

  return (
    <header className="retailer-header">
      <div className="header-left">
        <div className="logo">ðŸ›’ Product Tracking</div>
        <nav className="nav-links">
          <Link to="/" className="nav-link">Home</Link>
          <Link to="/retailer/dashboard" className="nav-link">Dashboard</Link>
          <Link to="/retailer/products" className="nav-link">Products</Link>
          <Link to="/retailer/view-farmers" className="nav-link">View Farmers</Link>
        </nav>
      </div>

      <div className="header-right">
        {retailer && (
          <span className="welcome-text">
            ðŸ‘‹ Welcome, <b>{retailer.storeName || retailer.name}</b>
          </span>
        )}
        <button onClick={handleLogout} className="logout-btn">
          Logout
        </button>
      </div>
    </header>
  );
};

export default RetailerHeader;
