import React, { useEffect, useState } from "react";
import { transportAPI } from "../services/api";

const TransportDashboard = () => {
  const transporter = JSON.parse(localStorage.getItem("transporter"));
  const transporterId = transporter?.id;

  const [orders, setOrders] = useState([]);

  useEffect(() => {
    if (!transporterId) return;

    transportAPI
      .getByTransporter(transporterId)
      .then((res) => setOrders(res.data));
  }, [transporterId]);

  return (
    <div>
      <h2>ðŸšš Transporter Dashboard</h2>

      {orders.length === 0 ? (
        <p>No transports assigned yet.</p>
      ) : (
        <ul>
          {orders.map((t) => (
            <li key={t.id}>
              <b>Order:</b> {t.order.id} <br />
              <b>Driver:</b> {t.driverName} <br />
              <b>Status:</b> {t.status}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default TransportDashboard;
