import React, { useEffect, useState } from "react";
import { retailerAPI, cartAPI } from "../services/api";
import { Link, useNavigate } from "react-router-dom";
import "./RetailerDashboard.css";

const RetailerDashboard = () => {
  const [farmers, setFarmers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [retailer, setRetailer] = useState(null);
  const [quantities, setQuantities] = useState({});
  const [expandedFarmer, setExpandedFarmer] = useState(null);
  const navigate = useNavigate();

  // ‚úÖ Load retailer info safely
  useEffect(() => {
    const storedRetailer = JSON.parse(localStorage.getItem("retailer"));
    if (!storedRetailer) {
      navigate("/retailer/login", { replace: true });
      return;
    }
    setRetailer(storedRetailer);
  }, [navigate]);

  // ‚úÖ Fetch farmers + products
  useEffect(() => {
    const fetchFarmers = async () => {
      try {
        const response = await retailerAPI.getAllFarmersWithProducts();
        setFarmers(response.data);
      } catch (error) {
        console.error("Error fetching farmers and products:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchFarmers();
  }, []);

  // ‚úÖ Logout handler
  const handleLogout = () => {
    localStorage.clear();
    navigate("/retailer/login", { replace: true });
  };

  // ‚úÖ Handle quantity change
  const handleQuantityChange = (productId, value) => {
    setQuantities((prev) => ({ ...prev, [productId]: Number(value) }));
  };

  // ‚úÖ Add to cart
  const handleAddToCart = async (productId) => {
    if (!retailer) {
      alert("Please log in first!");
      return;
    }
    const quantity = quantities[productId] || 1;

    try {
      const res = await cartAPI.addToCart({
        retailerId: retailer.id,
        productId,
        quantity,
      });
      alert("‚úÖ " + res.data);
    } catch (err) {
      console.error("Error adding to cart:", err);
      alert("‚ùå Failed to add product to cart.");
    }
  };

  // ‚úÖ Toggle farmer expand/collapse
  const toggleFarmer = (id) => {
    setExpandedFarmer((prev) => (prev === id ? null : id));
  };

  if (loading) return <p>Loading...</p>;

  return (
    <div className="dashboard">
      {/* ‚úÖ Header */}
      <header className="dashboard-header-bar">
        <nav className="nav-links">
          <Link to="/retailer/orders" className="nav-link">üì¶ My Orders</Link>
          <Link to="/retailer/cart" className="nav-link">üõçÔ∏è Cart</Link>
          {retailer && (
            <span className="welcome-text">
              Welcome, <b>{retailer.storeName || retailer.name}</b>
            </span>
          )}
          <button onClick={handleLogout} className="logout-btn">
            üö™ LOGOUT
          </button>
        </nav>
      </header>

      {/* ‚úÖ Page Header */}
      <div className="dashboard-header">
        <h1>Retailer Dashboard</h1>
        <p>Click a farmer‚Äôs name to view their products</p>
      </div>

      {/* ‚úÖ Farmer List */}
      <div className="farmer-list-section">
        {farmers.length === 0 ? (
          <p style={{ color: "gray" }}>No farmers registered yet.</p>
        ) : (
          farmers.map((farmer) => (
            <div key={farmer.id} className="farmer-card">
              <div
                className="farmer-header"
                onClick={() => toggleFarmer(farmer.id)}
              >
                <h3>
                  üë®‚Äçüåæ {farmer.name}
                  <span className="expand-icon">
                    {expandedFarmer === farmer.id ? "‚ñ≤" : "‚ñº"}
                  </span>
                </h3>
                <p style={{ color: "#555" }}>
                  {farmer.email} ‚Ä¢ {farmer.phone}
                </p>
              </div>

              {expandedFarmer === farmer.id && farmer.products.length > 0 && (
                <div className="farmer-products">
                  <table className="product-table">
                    <thead>
                      <tr>
                        <th>Product Name</th>
                        <th>Category</th>
                        <th>Quantity</th>
                        <th>Add to Cart</th>
                      </tr>
                    </thead>
                    <tbody>
                      {farmer.products.map((p) => (
                        <tr key={p.id}>
                          <td>{p.name}</td>
                          <td>{p.category}</td>
                          <td>
                            <input
                              type="number"
                              min="1"
                              value={quantities[p.id] || 1}
                              onChange={(e) =>
                                handleQuantityChange(p.id, e.target.value)
                              }
                              style={{
                                width: "60px",
                                textAlign: "center",
                                borderRadius: "6px",
                                border: "1px solid #ccc",
                              }}
                            />
                          </td>
                          <td>
                            <button
                              type="button"
                              onClick={() => handleAddToCart(p.id)}
                              className="add-cart-btn"
                            >
                              üõí Add
                            </button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              )}

              {expandedFarmer === farmer.id && farmer.products.length === 0 && (
                <p style={{ color: "gray", marginLeft: "20px" }}>
                  No products available.
                </p>
              )}
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default RetailerDashboard;
