import React, { useEffect, useState } from "react";
import { retailerAPI } from "../services/api";
import RetailerHeader from "./RetailerHeader"; // ‚úÖ import reusable header
import "./RetailerDashboard.css";

const RetailerDashboard = () => {
  const [farmers, setFarmers] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchFarmers = async () => {
      try {
        const response = await retailerAPI.getAllFarmersWithProducts();
        setFarmers(response.data);
      } catch (error) {
        console.error("Error fetching farmers and products:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchFarmers();
  }, []);

  if (loading) return <p>Loading...</p>;

  return (
    <div className="dashboard">
      <RetailerHeader /> {/* ‚úÖ Header at the top */}

      <div className="dashboard-header">
        <h1>Retailer Dashboard</h1>
        <p>View all farmers and their products</p>
      </div>

      {farmers.map((farmer) => (
        <div key={farmer.id} className="store-profile">
          <h3>üë®‚Äçüåæ {farmer.name}</h3>
          <p><b>Email:</b> {farmer.email}</p>
          <p><b>Phone:</b> {farmer.phone}</p>
          <p><b>Address:</b> {farmer.address}</p>

          {farmer.products && farmer.products.length > 0 ? (
            <table border="1" cellPadding="8" style={{ width: "100%" }}>
              <thead>
                <tr>
                  <th>Product Name</th>
                  <th>Category</th>
                  <th>Price</th>
                  <th>Description</th>
                  <th>Image</th>
                </tr>
              </thead>
              <tbody>
                {farmer.products.map((p) => (
                  <tr key={p.id}>
                    <td>{p.name}</td>
                    <td>{p.category}</td>
                    <td>‚Çπ{p.price}</td>
                    <td>{p.description}</td>
                    <td>
                      <img
                        src={`http://localhost:8080/uploads/${p.imagePath}`}
                        alt={p.name}
                        style={{
                          width: "80px",
                          height: "80px",
                          objectFit: "cover",
                          borderRadius: "5px",
                        }}
                      />
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            <p style={{ color: "gray" }}>No products available.</p>
          )}
        </div>
      ))}
    </div>
  );
};

export default RetailerDashboard;
