import React, { useEffect, useState } from "react";
import { retailerAPI, cartAPI } from "../services/api";
import { Link, useNavigate } from "react-router-dom";
import "./RetailerDashboard.css";

const RetailerDashboard = ({ user, logout }) => {
  const [farmers, setFarmers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [retailer, setRetailer] = useState(null);
  const [quantities, setQuantities] = useState({});
  const [selectedProduct, setSelectedProduct] = useState(null); // üÜï For modal
  const navigate = useNavigate();

  useEffect(() => {
    const storedRetailer = JSON.parse(localStorage.getItem("retailer"));
    if (!storedRetailer) {
      navigate("/retailer/login");
      return;
    }
    setRetailer(storedRetailer);
  }, [navigate]);

  useEffect(() => {
    const fetchFarmers = async () => {
      try {
        const response = await retailerAPI.getAllFarmersWithProducts();
        let data = response.data;

        // üåæ Sort: Farmers with products first
        data.sort((a, b) => {
          const aHas = a.products?.length > 0;
          const bHas = b.products?.length > 0;
          return aHas === bHas ? 0 : aHas ? -1 : 1;
        });

        setFarmers(data);
      } catch (error) {
        console.error("Error fetching farmers and products:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchFarmers();
  }, []);

  const handleLogout = () => {
    logout();
    localStorage.clear();
    sessionStorage.clear();
    navigate("/retailer/login", { replace: true });
    window.location.reload();
  };

  const handleQuantityChange = (productId, value) => {
    setQuantities((prev) => ({ ...prev, [productId]: Number(value) }));
  };

  const handleAddToCart = async (productId) => {
    if (!retailer) {
      alert("Please log in first!");
      return;
    }
    const quantity = quantities[productId] || 1;
    try {
      const res = await cartAPI.addToCart({
        retailerId: retailer.id,
        productId,
        quantity,
      });
      alert("‚úÖ " + res.data);
    } catch (err) {
      console.error("Error adding to cart:", err);
      alert("‚ùå Failed to add product to cart.");
    }
  };

  const handleImageClick = (product) => {
    setSelectedProduct(product); // üÜï open modal
  };

  const closeModal = () => {
    setSelectedProduct(null);
  };

  if (loading) return <p className="loading">üåæ Loading fresh produce...</p>;

  const farmersWithProducts = farmers.filter(f => f.products && f.products.length > 0);
  const farmersWithoutProducts = farmers.filter(f => !f.products || f.products.length === 0);

  return (
    <div className="dashboard">
      {/* Header */}
      <header className="dashboard-header-bar">
        <div className="logo">üåæ Product Tracking Blockchain</div>
        <nav className="nav-links">
          <Link to="/retailer/orders" className="nav-link">üì¶ My Orders</Link>
          <Link to="/retailer/cart" className="nav-link">üõí View Cart</Link>
          {retailer && <span className="welcome-text">Welcome, <b>{retailer.storeName || retailer.name}</b></span>}
          <button onClick={handleLogout} className="logout-btn">Logout</button>
        </nav>
      </header>

      {/* Title Section */}
      <section className="dashboard-title-section">
        <h1>Retailer Dashboard</h1>
        <p>Buy farm-fresh products directly from trusted farmers near you.</p>
        <hr className="section-divider" />
      </section>

      {/* üåø Farmers With Products */}
      <h2 className="section-label">üåø Farmers With Products</h2>
      <div className="farmer-list">
        {farmersWithProducts.map((farmer) => (
          <div key={farmer.id} className="farmer-card">
            <div className="farmer-header">
              <h3>{farmer.name}</h3>
              <span className="farmer-tag">Verified ‚úÖ</span>
            </div>

            <table className="product-table">
              <thead>
                <tr>
                  <th>Product</th>
                  <th>Category</th>
                  <th>Price</th>
                  <th>Description</th>
                  <th>Qty</th>
                  <th></th>
                </tr>
              </thead>
              <tbody>
                {farmer.products.map((p) => (
                  <tr key={p.id}>
                    <td className="product-name-cell">
                      <div className="product-info">
                        <img
                          src={p.imageUrl || p.image || "/placeholder.jpg"}
                          alt={p.name}
                          className="product-img"
                          onClick={() => handleImageClick(p)} // üñ±Ô∏è Click opens modal
                          onError={(e) => (e.target.src = "/placeholder.jpg")}
                        />
                        <span>{p.name}</span>
                      </div>
                    </td>
                    <td>{p.category}</td>
                    <td>‚Çπ{p.price}</td>
                    <td>{p.description}</td>
                    <td>
                      <input
                        type="number"
                        min="1"
                        value={quantities[p.id] || 1}
                        onChange={(e) => handleQuantityChange(p.id, e.target.value)}
                      />
                    </td>
                    <td>
                      <button
                        onClick={() => handleAddToCart(p.id)}
                        className="add-cart-btn"
                      >
                        üõí Add
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>

            <div className="farmer-info-section">
              <h4>Farmer Info</h4>
              <p><b>üìç Address:</b> {farmer.address}</p>
              <p><b>üìû Phone:</b> {farmer.phone}</p>
            </div>
          </div>
        ))}
      </div>

      {/* üö´ Farmers Without Products */}
      <h2 className="section-label no-products-title">üö´ Farmers Without Products</h2>
      <div className="farmer-list">
        {farmersWithoutProducts.map((farmer) => (
          <div key={farmer.id} className="farmer-card no-products-card">
            <h3>{farmer.name}</h3>
            <p className="no-products-msg">Currently, no products available from this farmer.</p>
            <div className="farmer-info-section">
              <p><b>üìç Address:</b> {farmer.address}</p>
              <p><b>üìû Phone:</b> {farmer.phone}</p>
            </div>
          </div>
        ))}
      </div>

      {/* üÜï Product Modal */}
      {selectedProduct && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <img
              src={selectedProduct.imageUrl || selectedProduct.image || "/placeholder.jpg"}
              alt={selectedProduct.name}
              className="modal-image"
            />
            <h2>{selectedProduct.name}</h2>
            <p><b>Category:</b> {selectedProduct.category}</p>
            <p><b>Description:</b> {selectedProduct.description}</p>
            <p className="modal-price">üí∞ ‚Çπ{selectedProduct.price}</p>
            <button
              className="modal-add-btn"
              onClick={() => handleAddToCart(selectedProduct.id)}
            >
              üõí Add to Cart
            </button>
            <button className="modal-close-btn" onClick={closeModal}>‚úñ Close</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default RetailerDashboard;
