import React, { useEffect, useState } from "react";
import { orderAPI } from "../services/api";
import { useNavigate } from "react-router-dom";
import "./RetailerOrders.css";

const RetailerOrders = () => {
  const [orders, setOrders] = useState([]);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [showModal, setShowModal] = useState(false);

  const retailer = JSON.parse(localStorage.getItem("retailer"));
  const navigate = useNavigate();

  // Load orders
  useEffect(() => {
    if (retailer?.id) loadOrders(retailer.id);
  }, [retailer]);

  const loadOrders = async (id) => {
    try {
      const res = await orderAPI.getRetailerOrders(id);
      setOrders(Array.isArray(res.data) ? res.data : []);
    } catch (err) {
      console.error("Error loading retailer orders:", err);
      setOrders([]);
    }
  };

  // View full order details
  const viewOrderDetails = async (orderId) => {
    try {
      const res = await orderAPI.getOrderDetails(orderId);
      setSelectedOrder(res.data);
      setShowModal(true);
    } catch (err) {
      console.error("Failed to load order details:", err);
      alert("‚ùå Could not load order details.");
    }
  };

  const closeModal = () => {
    setSelectedOrder(null);
    setShowModal(false);
  };

  // Confirm delivery
  const handleConfirm = async (orderId) => {
    try {
      await orderAPI.confirmReceipt(orderId);
      alert("‚úî Delivery confirmed!");

      setOrders((prev) =>
        prev.map((o) =>
          o.orderId === orderId ? { ...o, status: "DELIVERED" } : o
        )
      );

      closeModal();
    } catch (err) {
      console.error("Failed to confirm:", err);
      alert("‚ùå Failed to confirm delivery.");
    }
  };

  if (!retailer) {
    return (
      <div>
        <h3>No Retailer Logged In</h3>
      </div>
    );
  }

  return (
    <div className="orders-page">
      <div className="page-header">
        <button
          className="back-dashboard-btn"
          onClick={() => navigate("/retailer/dashboard")}
        >
          ‚Üê Back to Dashboard
        </button>
        <h1>üì¶ My Orders</h1>
      </div>

      {orders.length === 0 ? (
        <p className="empty-text">No orders found.</p>
      ) : (
        <table className="orders-table">
          <thead>
            <tr>
              <th>Order ID</th>
              <th>Date</th>
              <th>Total</th>
              <th>Payment Method</th>
              <th>Payment Status</th>
              <th>View</th>
            </tr>
          </thead>
          <tbody>
            {orders.map((o) => (
              <tr key={o.orderId}>
                <td>{o.orderId}</td>
                <td>{new Date(o.createdAt).toLocaleString()}</td>

                {/* SAFE PRICE FIX */}
                <td>‚Çπ{Number(o.totalAmount || 0).toFixed(2)}</td>

                <td>{o.paymentMethod}</td>
                <td
                  className={`status ${
                    o.paymentStatus === "PAID" ? "paid" : "pending"
                  }`}
                >
                  {o.paymentStatus}
                </td>
                <td>
                  <button
                    className="view-btn"
                    onClick={() => viewOrderDetails(o.orderId)}
                  >
                    üîç View
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {/* MODAL */}
      {showModal && selectedOrder && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal" onClick={(e) => e.stopPropagation()}>
            <h2>{`üßæ Order #${selectedOrder.orderId}`}</h2>

            <p>
              <b>Date:</b>{" "}
              {new Date(selectedOrder.createdAt).toLocaleString()}
            </p>

            <p>
              <b>Payment:</b> {selectedOrder.paymentMethod} /{" "}
              {selectedOrder.paymentStatus}
            </p>

            <p>
              <b>Status:</b> {selectedOrder.status}
            </p>

            <h3>üßë‚Äçüåæ Farmer</h3>
            <p>{selectedOrder.items?.[0]?.farmerName || "N/A"}</p>

            <h3>üõí Products</h3>

            <table className="details-table">
              <thead>
                <tr>
                  <th>Product</th>
                  <th>Qty</th>
                  <th>Unit Price</th>
                  <th>Total</th>
                </tr>
              </thead>
              <tbody>
                {selectedOrder.items?.map((item, i) => (
                  <tr key={i}>
                    <td>{item.productName}</td>
                    <td>{item.quantity}</td>

                    {/* SAFE PRICE FIX */}
                    <td>‚Çπ{Number(item.price || 0).toFixed(2)}</td>
                    <td>‚Çπ{Number(item.total || 0).toFixed(2)}</td>
                  </tr>
                ))}
              </tbody>
            </table>

            {/* SAFE GRAND TOTAL FIX */}
            <h3>
              üí∞ Grand Total: ‚Çπ
              {Number(selectedOrder.totalAmount || 0).toFixed(2)}
            </h3>

            <div className="modal-buttons">
              {selectedOrder.status !== "DELIVERED" && (
                <button
                  className="confirm-btn"
                  onClick={() => handleConfirm(selectedOrder.orderId)}
                >
                  ‚úî Confirm Delivery
                </button>
              )}

              <button className="close-btn" onClick={closeModal}>
                üîô Close
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default RetailerOrders;
