import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { productAPI } from "../services/api";


const ProductDetails = () => {
  const { id } = useParams(); // get ID from URL
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loadProduct = async () => {
      try {
        const res = await productAPI.getProductById(id);
        setProduct(res.data);
      } catch (err) {
        console.error("‚ùå Error fetching product:", err);
      } finally {
        setLoading(false);
      }
    };

    loadProduct();
  }, [id]);

  if (loading) return <h2>Loading product...</h2>;
  if (!product) return <h2>Product not found.</h2>;

  const imageUrl = `http://localhost:8080/uploads/${product.imagePath}`;
  const qrUrl = `http://localhost:8080/${product.qrCodePath}`;

  return (
    <div className="product-details-container">
      <h2>{product.name}</h2>

      <img src={imageUrl} alt={product.name} className="product-details-image" />

      <div className="info-box">
        <p><strong>Category:</strong> {product.category}</p>
        <p><strong>Price:</strong> ‚Çπ{product.price}</p>
        <p><strong>Batch No:</strong> {product.batchNumber}</p>
        <p><strong>Production Date:</strong> {product.productionDate}</p>
        <p><strong>Description:</strong> {product.description}</p>
      </div>

      <h3>QR Code</h3>
      <img src={qrUrl} alt="QR Code" className="qr-image" />

      <button
        className="download-btn"
        onClick={() => {
          const a = document.createElement("a");
          a.href = qrUrl;
          a.download = `QR_${product.name}.png`;
          a.click();
        }}
      >
        üì• Download QR
      </button>
    </div>
  );
};

export default ProductDetails;
