import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { retailerAPI, cartAPI } from "../services/api";
import "./FarmerProducts.css"; // üëà we'll add styling below

const FarmerProducts = () => {
  const { id } = useParams();
  const [products, setProducts] = useState([]);
  const [quantities, setQuantities] = useState({});
  const [farmer, setFarmer] = useState(null);
  const [retailer, setRetailer] = useState(null);
  const navigate = useNavigate();

  // ‚úÖ Fetch Farmer & Products
  useEffect(() => {
    const storedRetailer = JSON.parse(localStorage.getItem("retailer"));
    if (!storedRetailer) {
      alert("Please log in as a retailer first!");
      navigate("/retailer/login");
      return;
    }
    setRetailer(storedRetailer);

    retailerAPI
      .getFarmerById(id)
      .then((res) => {
        setFarmer(res.data);
        setProducts(res.data.products || []);
      })
      .catch((err) => console.error("Error fetching farmer:", err));
  }, [id, navigate]);

  // ‚úÖ Add product to cart
  const handleAddToCart = async (productId) => {
    if (!retailer) {
      alert("Retailer info not found. Please login again.");
      return;
    }

    const quantity = quantities[productId] || 1;

    try {
      await cartAPI.addToCart(retailer.id, productId, quantity);
      alert("‚úÖ Product added to cart successfully!");
    } catch (err) {
      console.error("Error adding to cart:", err);
      alert("‚ùå Failed to add to cart.");
    }
  };

  if (!farmer)
    return (
      <div className="loading-page">
        <h2>üåæ Loading farmer‚Äôs products...</h2>
      </div>
    );

  return (
    <div className="farmer-products-page">
      <header className="farmer-header">
        <h1>üë®‚Äçüåæ {farmer.name}'s Fresh Produce</h1>
        <p>
          <b>üìç Address:</b> {farmer.address}
        </p>
        <p className="subtitle">Buy directly from local farmers ‚Äî support sustainability üíö</p>
      </header>

      <hr className="divider" />

      {products.length === 0 ? (
        <div className="empty-section">
          <p>üö´ This farmer has no products yet.</p>
        </div>
      ) : (
        <table className="product-table">
          <thead>
            <tr>
              <th>PRODUCT</th>
              <th>CATEGORY</th>
              <th>PRICE</th>
              <th>DESCRIPTION</th>
              <th>QTY</th>
              <th>ACTION</th>
            </tr>
          </thead>
          <tbody>
            {products.map((p) => (
              <tr key={p.id} className="product-row">
                <td>{p.name}</td>
                <td>{p.category}</td>
                <td>‚Çπ{p.price.toFixed(2)}</td>
                <td>{p.description}</td>
                <td>
                  <input
                    type="number"
                    min="1"
                    value={quantities[p.id] || 1}
                    onChange={(e) =>
                      setQuantities({ ...quantities, [p.id]: e.target.value })
                    }
                    className="qty-input"
                  />
                </td>
                <td>
                  <button
                    className="add-btn"
                    onClick={() => handleAddToCart(p.id)}
                  >
                    üõí Add
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      <div className="bottom-actions">
        <button
          onClick={() => navigate("/retailer/cart")}
          className="view-cart-btn"
        >
          üõçÔ∏è View Cart
        </button>
        <button
          onClick={() => navigate("/retailer/dashboard")}
          className="back-btn"
        >
          ‚Üê Back to Farmers
        </button>
      </div>
    </div>
  );
};

export default FarmerProducts;
