import React, { useState } from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";

const TransportSignup = () => {
  const [form, setForm] = useState({
    name: "",
    email: "",
    password: "",
    phone: "",
    vehicleNo: "",
  });
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  // ‚úÖ Handle input changes
  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  // ‚úÖ Handle form submit
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      // ‚úÖ Send JSON payload properly
      const res = await axios.post(
        "http://localhost:8080/api/transporters/signup",
        form, // axios auto converts object ‚Üí JSON
        {
          headers: {
            "Content-Type": "application/json",
            Accept: "application/json",
          },
        }
      );

      console.log("‚úÖ Signup success:", res.data);
      alert("‚úÖ Registration successful! Redirecting to login...");
      navigate("/transport/login");
    } catch (err) {
      console.error("‚ùå Signup failed:", err);

      // ‚úÖ Handle known error codes clearly
      if (err.response) {
        const status = err.response.status;
        if (status === 415)
          alert("‚ö†Ô∏è Unsupported data type ‚Äî backend not accepting JSON.");
        else if (status === 400)
          alert("‚ö†Ô∏è Bad request ‚Äî please fill all fields correctly.");
        else if (status === 500)
          alert("üö® Server error ‚Äî check backend console logs.");
        else alert(`‚ùå Signup failed (Error ${status})`);
      } else {
        alert("‚ö†Ô∏è Network error. Backend might be offline.");
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-container">
      <h1>üöõ Transporter Registration</h1>

      <form onSubmit={handleSubmit}>
        <input
          name="name"
          placeholder="Full Name"
          onChange={handleChange}
          value={form.name}
          required
        />
        <input
          name="email"
          type="email"
          placeholder="Email"
          onChange={handleChange}
          value={form.email}
          required
        />
        <input
          name="password"
          type="password"
          placeholder="Password"
          onChange={handleChange}
          value={form.password}
          required
        />
        <input
          name="phone"
          placeholder="Phone Number"
          onChange={handleChange}
          value={form.phone}
          required
        />
        <input
          name="vehicleNo"
          placeholder="Vehicle Number"
          onChange={handleChange}
          value={form.vehicleNo}
          required
        />

        <button type="submit" disabled={loading}>
          {loading ? "‚è≥ Registering..." : "Register"}
        </button>
      </form>

      <p>
        Already registered? <Link to="/transport/login">Login here</Link>
      </p>
    </div>
  );
};

export default TransportSignup;
