// ==================================================
// src/App.js - Cleaned, working, with proper redirects
// ==================================================

import { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from "react-router-dom";

import "./App.css";

// ---------- Common ----------
import Home from "./components/Home";

// ---------- Farmer ----------
import FarmerLogin from "./components/FarmerLogin";
import FarmerSignup from "./components/FarmerSignup";
import FarmerDashboard from "./components/FarmerDashboard";
import AddProduct from "./components/AddProduct";
import ProductList from "./components/ProductList";
import FarmerAssignTransport from "./components/FarmerAssignTransport";

// ---------- Retailer ----------
import RetailerLogin from "./components/RetailerLogin";
import RetailerSignup from "./components/RetailerSignup";
import RetailerDashboard from "./components/RetailerDashboard";
import RetailerCart from "./components/RetailerCart";
import RetailerOrders from "./components/RetailerOrders";
import PaymentPage from "./components/PaymentPage";
import FarmerProducts from "./components/FarmerProducts";

// ðŸ‘‰ NEW Retailer Components (ADDED ONLY)
import ConfirmReceipt from "./components/ConfirmReceipt";
import OrderList from "./components/OrderList";

// ---------- Transporter ----------
import TransportSignup from "./components/TransportSignup";
import TransportLogin from "./components/TransportLogin";
import TransportDashboard from "./components/TransportDashboard";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);
  const [userType, setUserType] = useState(null);

  // ---------- Load auth state ----------
  useEffect(() => {
    const auth = localStorage.getItem("isAuthenticated");
    const userData = localStorage.getItem("user");
    const type = localStorage.getItem("userType");

    if (auth === "true" && userData && type) {
      setIsAuthenticated(true);
      setUser(JSON.parse(userData));
      setUserType(type);
    }
  }, []);

  // ---------- LOGIN ----------
  const login = (userData, type) => {
    setIsAuthenticated(true);
    setUser(userData);
    setUserType(type);
    localStorage.setItem("isAuthenticated", "true");
    localStorage.setItem("user", JSON.stringify(userData));
    localStorage.setItem("userType", type);
  };

  // ---------- LOGOUT ----------
  const logout = () => {
    setIsAuthenticated(false);
    setUser(null);
    setUserType(null);
    localStorage.clear();
    sessionStorage.clear();
  };

  // ---------- Protected Route ----------
  const ProtectedRoute = ({ children, allowedType }) => {
    const auth = localStorage.getItem("isAuthenticated");
    const storedType = localStorage.getItem("userType");

    if (auth !== "true") {
      return <Navigate to={`/${allowedType}/login`} replace />;
    }

    if (storedType !== allowedType) {
      return <Navigate to={`/${storedType}/dashboard`} replace />;
    }

    return children;
  };

  return (
    <Router>
      <Routes>
        {/* ---------- HOME ---------- */}
        <Route
          path="/"
          element={
            <Home
              isAuthenticated={isAuthenticated}
              user={user}
              userType={userType}
              logout={logout}
            />
          }
        />

        {/* ---------- FARMER ROUTES ---------- */}
        <Route
          path="/farmer/login"
          element={
            !isAuthenticated ? (
              <FarmerLogin login={(data) => login(data, "farmer")} />
            ) : (
              <Navigate to="/farmer/dashboard" replace />
            )
          }
        />
        <Route
          path="/farmer/signup"
          element={
            !isAuthenticated ? (
              <FarmerSignup />
            ) : (
              <Navigate to="/farmer/dashboard" replace />
            )
          }
        />
        <Route
          path="/farmer/dashboard"
          element={
            <ProtectedRoute allowedType="farmer">
              <FarmerDashboard user={user} logout={logout} />
            </ProtectedRoute>
          }
        />
        <Route
          path="/farmer/add-product"
          element={
            <ProtectedRoute allowedType="farmer">
              <AddProduct />
            </ProtectedRoute>
          }
        />
        <Route
          path="/farmer/products"
          element={
            <ProtectedRoute allowedType="farmer">
              <ProductList />
            </ProtectedRoute>
          }
        />
        <Route
          path="/farmer/assign-transport/:orderId"
          element={
            <ProtectedRoute allowedType="farmer">
              <FarmerAssignTransport />
            </ProtectedRoute>
          }
        />

        {/* ---------- RETAILER ROUTES ---------- */}
        <Route
          path="/retailer/login"
          element={
            !isAuthenticated ? (
              <RetailerLogin login={(data) => login(data, "retailer")} />
            ) : (
              <Navigate to="/retailer/dashboard" replace />
            )
          }
        />
        <Route
          path="/retailer/signup"
          element={
            !isAuthenticated ? (
              <RetailerSignup />
            ) : (
              <Navigate to="/retailer/dashboard" replace />
            )
          }
        />
        <Route
          path="/retailer/dashboard"
          element={
            <ProtectedRoute allowedType="retailer">
              <RetailerDashboard user={user} logout={logout} />
            </ProtectedRoute>
          }
        />
        <Route
          path="/retailer/farmer/:id"
          element={
            <ProtectedRoute allowedType="retailer">
              <FarmerProducts />
            </ProtectedRoute>
          }
        />
        <Route
          path="/retailer/cart"
          element={
            <ProtectedRoute allowedType="retailer">
              <RetailerCart />
            </ProtectedRoute>
          }
        />

        {/* Existing retailer orders page */}
        <Route
          path="/retailer/orders"
          element={
            <ProtectedRoute allowedType="retailer">
              <RetailerOrders />
            </ProtectedRoute>
          }
        />

        {/* âœ… NEW: Retailer Full Order List */}
        <Route
          path="/retailer/order-list"
          element={
            <ProtectedRoute allowedType="retailer">
              <OrderList retailerId={JSON.parse(localStorage.getItem("retailer"))?.id} />
            </ProtectedRoute>
          }
        />

        {/* âœ… NEW: Retailer Confirm Receipt Page */}
        <Route
          path="/retailer/delivered-orders"
          element={
            <ProtectedRoute allowedType="retailer">
              <ConfirmReceipt retailerId={JSON.parse(localStorage.getItem("retailer"))?.id} />
            </ProtectedRoute>
          }
        />

        <Route
          path="/payment"
          element={
            <ProtectedRoute allowedType="retailer">
              <PaymentPage />
            </ProtectedRoute>
          }
        />

        {/* ---------- TRANSPORTER ROUTES ---------- */}
        <Route
          path="/transport/signup"
          element={
            !isAuthenticated ? (
              <TransportSignup />
            ) : (
              <Navigate to="/transport/dashboard" replace />
            )
          }
        />
        <Route
          path="/transport/login"
          element={
            !isAuthenticated ? (
              <TransportLogin login={(data) => login(data, "transporter")} />
            ) : (
              <Navigate to="/transport/dashboard" replace />
            )
          }
        />
        <Route
          path="/transport/dashboard"
          element={
            <ProtectedRoute allowedType="transporter">
              <TransportDashboard user={user} logout={logout} />
            </ProtectedRoute>
          }
        />

        {/* ---------- FALLBACK ---------- */}
        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>
    </Router>
  );
}

export default App;
