import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import AddProduct from './AddProduct';
import ProductList from './ProductList';

const FarmerDashboard = ({ farmer, logout }) => {
  const [productsUpdated, setProductsUpdated] = useState(0);

  const handleProductAdded = () => {
    // Trigger refresh of product list
    setProductsUpdated(prev => prev + 1);
  };

  return (
    <div>
      <nav className="navbar">
        <div className="nav-brand">Product Tracking using Blockchain</div>
        <div className="nav-links">
          <Link to="/" className="nav-link">HOME</Link>
          <span style={{ color: 'white' }}>Welcome, {farmer?.name}</span>
          <button onClick={logout} className="logout-btn">LOGOUT</button>
        </div>
      </nav>

      <div className="dashboard">
        <div className="dashboard-header">
          <h1>Farmer Dashboard</h1>
          <p>Manage your products and track them using blockchain technology</p>
        </div>

        <div className="dashboard-content">
          <div className="add-product-section">
            <AddProduct onProductAdded={handleProductAdded} />
          </div>
          <div className="product-list-section">
            <ProductList key={productsUpdated} />
          </div>
        </div>
      </div>
    </div>
  );
};

export default FarmerDashboard;
