import React, { useEffect, useState } from "react";
import { orderAPI } from "../services/api";
import { useLocation, useNavigate } from "react-router-dom";
import "./PaymentPage.css";

const PaymentPage = () => {
  const [method, setMethod] = useState("UPI");
  const [order, setOrder] = useState(null);
  const location = useLocation();
  const navigate = useNavigate();

  // ‚úÖ Retrieve order info from navigation state
  useEffect(() => {
    if (location.state && location.state.order) {
      setOrder(location.state.order);
    } else {
      navigate("/retailer/cart");
    }
  }, [location, navigate]);

  const handlePayment = async () => {
    try {
      await orderAPI.makePayment(order.id, method);
      alert(`‚úÖ Payment successful via ${method}!`);
      navigate("/retailer/orders");
    } catch (err) {
      console.error("Payment failed:", err);
      alert("‚ùå Payment failed. Try again.");
    }
  };

  if (!order) return <p>Loading...</p>;

  return (
    <div className="payment-container">
      <h2>üí∞ Payment for Order #{order.id}</h2>
      <p>Total Amount: <b>‚Çπ{order.totalAmount.toFixed(2)}</b></p>

      <div className="payment-options">
        <label>
          <input
            type="radio"
            name="payment"
            value="UPI"
            checked={method === "UPI"}
            onChange={(e) => setMethod(e.target.value)}
          />
          üí≥ Pay via UPI
        </label>

        <label>
          <input
            type="radio"
            name="payment"
            value="CASH"
            checked={method === "CASH"}
            onChange={(e) => setMethod(e.target.value)}
          />
          üíµ Cash on Delivery
        </label>
      </div>

      {method === "UPI" && (
        <div className="upi-section">
          <p>Scan this QR or use your UPI App</p>
          <img
            src="/upi_qr_sample.png"
            alt="UPI QR Code"
            className="upi-qr"
          />
        </div>
      )}

      <button className="pay-btn" onClick={handlePayment}>
        ‚úÖ Confirm Payment
      </button>
    </div>
  );
};

export default PaymentPage;
