import React, { useEffect, useState } from "react";
import { transportAPI } from "../services/api";
import "./TransportDashboard.css";

const TransportDashboard = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const transporter = JSON.parse(localStorage.getItem("transporter"));

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        if (transporter) {
          const res = await transportAPI.getByTransporter(transporter.id);
          setOrders(res.data);
        }
      } catch (error) {
        console.error("‚ùå Failed to fetch transports:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchOrders();
  }, [transporter]);

  if (loading) return <p>Loading transports...</p>;

  return (
    <div className="dashboard-container">
      <h2>üöö Transporter Dashboard</h2>
      {orders.length > 0 ? (
        <ul>
          {orders.map((t) => (
            <li key={t.id}>
              <b>Order ID:</b> {t.orderId} <br />
              <b>Status:</b> {t.status}
            </li>
          ))}
        </ul>
      ) : (
        <p>No transports assigned yet.</p>
      )}
    </div>
  );
};

export default TransportDashboard;
