import React, { useEffect, useState, useCallback } from "react";
import { retailerAPI, userAPI } from "../services/api";
import { useParams, useNavigate } from "react-router-dom";

export default function UserRetailerProducts() {
  const { retailerId } = useParams();
  const navigate = useNavigate();

  const [products, setProducts] = useState([]);

  // Fetch retailer products
  const loadRetailerProducts = useCallback(async () => {
    try {
      const res = await retailerAPI.getRetailerProducts(retailerId);
      setProducts(res.data);
    } catch (err) {
      console.error("Failed to load retailer products:", err);
    }
  }, [retailerId]);

  useEffect(() => {
    loadRetailerProducts();
  }, [loadRetailerProducts]);

  // User Buys Product
  const handleBuy = async (productId) => {
    try {
      const storedUser = localStorage.getItem("user");

      if (!storedUser) {
        alert("Please login to continue");
        navigate("/user/login");
        return;
      }

      const user = JSON.parse(storedUser);

      const payload = {
        userId: user.id,
        productId,
        quantity: 1,
      };

      await userAPI.buyProduct(payload);

      alert("Purchase successful!");
      loadRetailerProducts();

    } catch (err) {
      console.error("Purchase failed:", err);
      alert("Purchase failed!");
    }
  };

  return (
    <div className="retailer-products-page">
      <button className="back-btn" onClick={() => navigate(-1)}>← Back</button>

      <h2>Retailer Products</h2>

      {products.length === 0 && (
        <p>No products found for this retailer.</p>
      )}

      <div className="product-grid">
        {products.map((p) => (
          <div className="product-card" key={p.id}>
            {p.imagePath && (
              <img
                src={`http://localhost:8080/uploads/${p.imagePath}`}
                alt={p.name}
                className="product-img"
              />
            )}

            <h3>{p.name}</h3>
            <p><b>Category:</b> {p.category}</p>
            <p><b>Price:</b> ₹{p.price}</p>
            <p><b>Stock:</b> {p.quantity}</p>

            <button
              className="buy-btn"
              disabled={p.quantity <= 0}
              onClick={() => handleBuy(p.id)}
            >
              {p.quantity > 0 ? "Buy" : "Out of Stock"}
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}
