import React, { useState, useEffect } from "react";
import { transportAPI, transporterAPI } from "../services/api";
import { useParams, useNavigate } from "react-router-dom";

const FarmerAssignTransport = () => {
  const { orderId } = useParams();
  const navigate = useNavigate();

  const [transporters, setTransporters] = useState([]);
  const [form, setForm] = useState({
    driverName: "",
    contact: "",
    vehicleNo: "",
    transporterId: "",
  });

  useEffect(() => {
    transporterAPI.getAll().then((res) => setTransporters(res.data));
  }, []);

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const assignTransport = async (e) => {
    e.preventDefault();

    if (!form.transporterId) {
      alert("Select a transporter");
      return;
    }

    try {
      await transportAPI.assignTransport(
        orderId,
        form.transporterId,
        form
      );

      alert("üöö Transport assigned successfully!");
      navigate("/farmer/dashboard");
    } catch (err) {
      console.error(err);
      alert("‚ùå Failed to assign transport");
    }
  };

  return (
    <div className="assign-transport-container">
      <h2>üöö Assign Transport</h2>

      <form onSubmit={assignTransport}>
        <label>Choose Transporter:</label>
        <select
          name="transporterId"
          value={form.transporterId}
          onChange={handleChange}
        >
          <option value="">-- Select Transporter --</option>
          {transporters.map((t) => (
            <option key={t.id} value={t.id}>
              {t.name} ({t.vehicleNo})
            </option>
          ))}
        </select>

        <input
          type="text"
          name="driverName"
          placeholder="Driver Name"
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="contact"
          placeholder="Driver Contact"
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="vehicleNo"
          placeholder="Vehicle Number"
          onChange={handleChange}
          required
        />

        <button type="submit">Assign Transport</button>
      </form>
    </div>
  );
};

export default FarmerAssignTransport;
