import React, { useEffect, useState } from "react";
import { retailerAPI, cartAPI } from "../services/api";
import { Link, useNavigate } from "react-router-dom";
import "./RetailerDashboard.css";

const RetailerDashboard = () => {
  const [farmers, setFarmers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [retailer, setRetailer] = useState(null);
  const [quantities, setQuantities] = useState({});
  const navigate = useNavigate();

  // ‚úÖ Fetch retailer info safely
  useEffect(() => {
    const storedRetailer = JSON.parse(localStorage.getItem("retailer"));
    if (!storedRetailer) {
      // prevent re-render loop
      setTimeout(() => navigate("/retailer/login"), 0);
      return;
    }
    setRetailer(storedRetailer);
  }, []);

  // ‚úÖ Logout handler
  const handleLogout = () => {
    localStorage.clear();
    navigate("/retailer/login");
  };

  // ‚úÖ Quantity change
  const handleQuantityChange = (productId, value) => {
    setQuantities((prev) => ({ ...prev, [productId]: value }));
  };

  // ‚úÖ Add to cart
  const handleAddToCart = async (productId) => {
    if (!retailer) return;
    const quantity = quantities[productId] || 1;

    try {
      await cartAPI.addToCart({
        retailerId: retailer.id,
        productId,
        quantity,
      });
      alert("‚úÖ Product added to cart!");
    } catch (err) {
      console.error("Error adding to cart:", err);
      alert("‚ùå Failed to add product to cart.");
    }
  };

  if (loading) return <p>Loading...</p>;

  return (
    <div className="retailer-dashboard">
      <nav className="navbar">
        <div className="nav-brand">Product Tracking using Blockchain</div>
        <div className="nav-links">
          <Link to="/" className="nav-link">HOME</Link>
          <button onClick={handleLogout} className="nav-link logout-btn">LOGOUT</button>
        </div>
      </nav>
      {/* ‚úÖ Page Header */}
      <div className="dashboard-header">
        <h1>Retailer Dashboard</h1>
        <p>View all farmers and their available products</p>
      </div>

      {/* ‚úÖ Farmer + Product Table */}
      {farmers.map((farmer) => (
        <div key={farmer.id} className="store-profile">
          <h3>üë®‚Äçüåæ {farmer.name}</h3>
          <p><b>Email:</b> {farmer.email}</p>
          <p><b>Phone:</b> {farmer.phone}</p>
          <p><b>Address:</b> {farmer.address}</p>

          {farmer.products && farmer.products.length > 0 ? (
            <table className="product-table">
              <thead>
                <tr>
                  <th>Product Name</th>
                  <th>Category</th>
                  <th>Price</th>
                  <th>Description</th>
                  <th>Quantity</th>
                  <th>Add to Cart</th>
                </tr>
              </thead>
              <tbody>
                {farmer.products.map((p) => (
                  <tr key={p.id}>
                    <td>{p.name}</td>
                    <td>{p.category}</td>
                    <td>‚Çπ{p.price}</td>
                    <td>{p.description}</td>
                    <td>
                      <input
                        type="number"
                        min="1"
                        value={quantities[p.id] || 1}
                        onChange={(e) =>
                          handleQuantityChange(p.id, e.target.value)
                        }
                        style={{ width: "60px", textAlign: "center" }}
                      />
                    </td>
                    <td>
                      <button
                        onClick={() => handleAddToCart(p.id)}
                        className="add-cart-btn"
                      >
                        üõí Add
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            <p style={{ color: "gray" }}>No products available.</p>
          )}
        </div>
      ))}
    </div>
  );
};

export default RetailerDashboard;
