import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { transporterAuthAPI } from "../services/api"; // ✅ use your centralized API file

const TransportLogin = () => {
  const [formData, setFormData] = useState({ email: "", password: "" });
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  // ✅ Handle input change
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // ✅ Handle login submit
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");
    setSuccess("");

    try {
      const res = await transporterAuthAPI.login(formData);
      localStorage.setItem("transporter", JSON.stringify(res.data));
      localStorage.setItem("isAuthenticated", "true");
      localStorage.setItem("userType", "transporter");
      localStorage.setItem("user", JSON.stringify(res.data));

      setSuccess("✅ Login successful! Redirecting to dashboard...");
      setTimeout(() => navigate("/transport/dashboard"), 1500);
    } catch (err) {
      console.error("Login Error:", err);
      setError(err.response?.data?.message || "Invalid credentials");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      {/* ---------- Navbar ---------- */}
      <nav className="navbar">
        <div className="nav-brand">Product Tracking using Blockchain</div>
        <div className="nav-links">
          <Link to="/" className="nav-link">
            HOME
          </Link>
          <Link to="/transport/signup" className="nav-link">
            TRANSPORT SIGNUP
          </Link>
        </div>
      </nav>

      {/* ---------- Login Form ---------- */}
      <div className="form-container">
        <h2>Transporter Login</h2>

        {/* Alerts */}
        {error && <div className="alert alert-error">{error}</div>}
        {success && <div className="alert alert-success">{success}</div>}

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Email:</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label>Password:</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
            />
          </div>

          <button type="submit" className="submit-btn" disabled={loading}>
            {loading ? "Logging in..." : "Login"}
          </button>
        </form>

        <p style={{ textAlign: "center", marginTop: "1rem" }}>
          Don't have an account?{" "}
          <Link to="/transport/signup">Signup here</Link>
        </p>
      </div>
    </div>
  );
};

export default TransportLogin;
