import React, { useEffect, useState } from "react";
import { orderAPI } from "../services/api";
import { useNavigate } from "react-router-dom";
import "./RetailerOrders.css";

const RetailerOrders = () => {
  const [orders, setOrders] = useState([]);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const retailer = JSON.parse(localStorage.getItem("retailer"));
  const navigate = useNavigate();

  // ‚úÖ Fetch all orders for the logged-in retailer
  useEffect(() => {
    if (retailer) fetchOrders(retailer.id);
  }, [retailer]);

  const fetchOrders = async (id) => {
    try {
      const res = await orderAPI.getOrders(id);
      const data = Array.isArray(res.data) ? res.data : [];
      setOrders(data);
    } catch (err) {
      console.error("Error fetching orders:", err);
      setOrders([]);
    }
  };

  const viewOrderDetails = async (orderId) => {
    try {
      const res = await orderAPI.getOrderDetails(orderId);
      console.log("üì¶ Order Details Response:", res.data);
      setSelectedOrder(res.data);
      setShowModal(true);
    } catch (err) {
      console.error("Error fetching order details:", err);
      alert("‚ùå Failed to fetch order details.");
    }
  };

  const closeModal = () => {
    setShowModal(false);
    setSelectedOrder(null);
  };

  const goBackToDashboard = () => {
    navigate("/retailer/dashboard");
  };

  return (
    <div className="orders-page">
      {/* üîô Header Section */}
      <div className="page-header">
        <button className="back-dashboard-btn" onClick={goBackToDashboard}>
          üîô Back to Dashboard
        </button>
        <h1>üì¶ My Orders</h1>
      </div>

      {/* üßæ Order Table */}
      {orders.length === 0 ? (
        <p className="empty-text">No orders found.</p>
      ) : (
        <table className="orders-table">
          <thead>
            <tr>
              <th>Order ID</th>
              <th>Date</th>
              <th>Total Amount</th>
              <th>Payment Method</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {orders.map((o) => (
              <tr key={o.id}>
                <td>#{o.id}</td>
                <td>{new Date(o.createdAt).toLocaleString()}</td>
                <td>‚Çπ{o.totalAmount.toFixed(2)}</td>
                <td>{o.paymentMethod}</td>
                <td
                  className={`status ${
                    o.paymentStatus === "Paid" ? "paid" : "pending"
                  }`}
                >
                  {o.paymentStatus}
                </td>
                <td>
                  <button
                    className="view-btn"
                    onClick={() => viewOrderDetails(o.id)}
                  >
                    üîç View Details
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {/* ‚úÖ Order Details Modal */}
      {showModal && selectedOrder && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal" onClick={(e) => e.stopPropagation()}>
            <h2>üßæ Order #{selectedOrder.id} Details</h2>
            <div className="order-meta">
              <p>
                <b>Date:</b>{" "}
                {new Date(selectedOrder.createdAt).toLocaleString()}
              </p>
              <p>
                <b>Payment Method:</b> {selectedOrder.paymentMethod}
              </p>
              <p>
                <b>Status:</b>{" "}
                <span
                  className={
                    selectedOrder.paymentStatus === "Paid"
                      ? "status paid"
                      : "status pending"
                  }
                >
                  {selectedOrder.paymentStatus}
                </span>
              </p>
            </div>

            {/* üè™ Retailer & üë®‚Äçüåæ Farmer Info */}
            <div className="order-party-info">
              <p>
                <b>üè™ Retailer:</b>{" "}
                {selectedOrder.retailer?.storeName ||
                  selectedOrder.retailer?.name ||
                  "N/A"}
              </p>
              <p>
                <b>üë®‚Äçüåæ Farmer:</b>{" "}
                {selectedOrder.items?.[0]?.farmerName || "N/A"}
              </p>
            </div>

            {/* üõí Product List */}
            <h3>üõí Products</h3>
            <table className="details-table">
              <thead>
                <tr>
                  <th>Product</th>
                  <th>Farmer</th>
                  <th>Quantity</th>
                  <th>Price (each)</th>
                  <th>Total</th>
                </tr>
              </thead>
              <tbody>
                {selectedOrder.items.map((item, index) => (
                  <tr key={index}>
                    <td>{item.productName}</td>
                    <td>{item.farmerName}</td>
                    <td>{item.quantity}</td>
                    <td>‚Çπ{item.price.toFixed(2)}</td>
                    <td>‚Çπ{item.total.toFixed(2)}</td>
                  </tr>
                ))}
              </tbody>
            </table>

            <h3 className="total-amount">
              üí∞ Total Amount: ‚Çπ{selectedOrder.totalAmount.toFixed(2)}
            </h3>

            <div className="modal-buttons">
              <button className="back-btn" onClick={closeModal}>
                üîô Back
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default RetailerOrders;
