import React, { useState } from 'react';
import { productAPI } from '../services/api';

const AddProduct = ({ onProductAdded }) => {
  const [formData, setFormData] = useState({
    name: '',
    category: '',
    price: '',
    batchNumber: '',
    productionDate: '',
    description: ''
  });
  const [image, setImage] = useState(null);
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleImageChange = (e) => {
    setImage(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    try {
      const submitData = new FormData();

      // Append product data as JSON string
      const productData = {
        ...formData,
        price: parseFloat(formData.price),
        productionDate: formData.productionDate ? new Date(formData.productionDate).toISOString() : null
      };
      submitData.append('product', new Blob([JSON.stringify(productData)], {
        type: 'application/json'
      }));

      // Append image if selected
      if (image) {
        submitData.append('image', image);
      }

      await productAPI.addProduct(submitData);
      setMessage('Product added successfully!');

      // Reset form
      setFormData({
        name: '',
        category: '',
        price: '',
        batchNumber: '',
        productionDate: '',
        description: ''
      });
      setImage(null);

      // Reset file input
      const fileInput = document.getElementById('imageInput');
      if (fileInput) fileInput.value = '';

      // Notify parent component to refresh product list
      if (onProductAdded) {
        onProductAdded();
      }

    } catch (error) {
      setMessage('Error adding product: ' + (error.response?.data || error.message));
      console.error('Add product error:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="form-container">
      <h2>Add New Product</h2>
      {message && (
        <div className={message.includes('Error') ? 'alert alert-error' : 'alert alert-success'}>
          {message}
        </div>
      )}
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Product Name:</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
            placeholder="Enter product name"
          />
        </div>
        <div className="form-group">
          <label>Category:</label>
          <input
            type="text"
            name="category"
            value={formData.category}
            onChange={handleChange}
            required
            placeholder="e.g., Vegetables, Fruits, Dairy"
          />
        </div>
        <div className="form-group">
          <label>Price ($):</label>
          <input
            type="number"
            step="0.01"
            min="0"
            name="price"
            value={formData.price}
            onChange={handleChange}
            required
            placeholder="0.00"
          />
        </div>
        <div className="form-group">
          <label>Batch Number:</label>
          <input
            type="text"
            name="batchNumber"
            value={formData.batchNumber}
            onChange={handleChange}
            required
            placeholder="Unique batch identifier"
          />
        </div>
        <div className="form-group">
          <label>Production Date:</label>
          <input
            type="datetime-local"
            name="productionDate"
            value={formData.productionDate}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label>Product Image:</label>
          <input
            id="imageInput"
            type="file"
            accept="image/*"
            onChange={handleImageChange}
          />
          <small>Optional: Upload a product image (JPEG, PNG, etc.)</small>
        </div>
        <div className="form-group">
          <label>Description:</label>
          <textarea
            name="description"
            value={formData.description}
            onChange={handleChange}
            rows="3"
            placeholder="Product description, features, or notes..."
          />
        </div>
        <button type="submit" className="submit-btn" disabled={loading}>
          {loading ? 'Adding Product...' : 'Add Product'}
        </button>
      </form>
    </div>
  );
};

export default AddProduct;
