import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import FarmerRegister from "./components/FarmerRegister";
import FarmerLogin from "./components/FarmerLogin";
import FarmerDashboard from "./components/FarmerDashboard";
import ProductManagement from "./components/ProductManagement";
import ProductView from "./components/ProductView";
import "./App.css";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const checkAuth = async () => {
      try {
        // For now, check if session cookie exists
        setIsAuthenticated(document.cookie.includes("JSESSIONID"));
      } catch (error) {
        console.error("Auth check failed:", error);
        setIsAuthenticated(false);
      } finally {
        setLoading(false);
      }
    };

    checkAuth();
  }, []);

  if (loading) {
    return <div className="loading">Loading FarmChain Tracker...</div>;
  }

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route
            path="/login"
            element={
              !isAuthenticated ? (
                <FarmerLogin onAuthSuccess={() => setIsAuthenticated(true)} />
              ) : (
                <Navigate to="/dashboard" />
              )
            }
          />
          <Route
            path="/register"
            element={
              !isAuthenticated ? (
                <FarmerRegister />
              ) : (
                <Navigate to="/dashboard" />
              )
            }
          />
          <Route
            path="/dashboard"
            element={
              isAuthenticated ? (
                <FarmerDashboard onLogout={() => setIsAuthenticated(false)} />
              ) : (
                <Navigate to="/login" />
              )
            }
          />
          <Route
            path="/products"
            element={
              isAuthenticated ? (
                <ProductManagement />
              ) : (
                <Navigate to="/login" />
              )
            }
          />
          <Route
            path="/products/:id"
            element={
              isAuthenticated ? (
                <ProductView />
              ) : (
                <Navigate to="/login" />
              )
            }
          />
          <Route
            path="/"
            element={<Navigate to={isAuthenticated ? "/dashboard" : "/login"} />}
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
