import React, { useState, useEffect } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import './ProductView.css';

const ProductView = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [product, setProduct] = useState(null);
    const [loading, setLoading] = useState(true);
    const [showHistoryForm, setShowHistoryForm] = useState(false);
    const [historyForm, setHistoryForm] = useState({
        action: '',
        description: ''
    });

    useEffect(() => {
        fetchProduct();
    }, [id]);

    const fetchProduct = async () => {
        try {
            const response = await fetch(`http://localhost:8080/api/products/${id}`, {
                credentials: 'include'
            });

            if (response.ok) {
                const productData = await response.json();
                setProduct(productData);
            } else {
                console.error('Product not found');
                navigate('/products');
            }
        } catch (error) {
            console.error('Error fetching product:', error);
        } finally {
            setLoading(false);
        }
    };

    const handleHistorySubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await fetch(`http://localhost:8080/api/products/${id}/history`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(historyForm),
                credentials: 'include'
            });

            if (response.ok) {
                setShowHistoryForm(false);
                setHistoryForm({ action: '', description: '' });
                fetchProduct(); // Refresh product data
                alert('History entry added successfully!');
            }
        } catch (error) {
            console.error('Error adding history:', error);
        }
    };

    const markAsHarvested = async () => {
        try {
            const response = await fetch(`http://localhost:8080/api/products/${id}/harvest`, {
                method: 'POST',
                credentials: 'include'
            });

            if (response.ok) {
                fetchProduct();
                alert('Product marked as harvested!');
            }
        } catch (error) {
            console.error('Error marking as harvested:', error);
        }
    };

    const formatDate = (dateString) => {
        if (!dateString) return 'Not set';
        return new Date(dateString).toLocaleDateString();
    };

    const formatDateTime = (dateString) => {
        if (!dateString) return '';
        return new Date(dateString).toLocaleString();
    };

    if (loading) {
        return <div className="loading">Loading product details...</div>;
    }

    if (!product) {
        return <div className="error">Product not found</div>;
    }

    return (
        <div className="product-view">
            <div className="page-header">
                <Link to="/products" className="back-link">← Back to Products</Link>
                <h1>Product Details</h1>
            </div>

            <div className="product-details-container">
                <div className="product-info-card">
                    <div className="product-header">
                        <h2>{product.productName}</h2>
                        <div className="product-meta">
                            <span className={`product-type-badge ${product.productType?.toLowerCase()}`}>
                                {product.productType}
                            </span>
                            <span className={`status-badge ${product.harvestDate ? 'harvested' : 'growing'}`}>
                                {product.harvestDate ? 'Harvested' : 'Growing'}
                            </span>
                        </div>
                    </div>

                    {product.description && (
                        <p className="product-description">{product.description}</p>
                    )}

                    <div className="details-grid">
                        <div className="detail-item">
                            <label>Product ID:</label>
                            <span>{product.productId}</span>
                        </div>

                        <div className="detail-item">
                            <label>Blockchain Hash:</label>
                            <span className="blockchain-hash">{product.blockchainHash}</span>
                        </div>

                        <div className="detail-item">
                            <label>Planting Date:</label>
                            <span>{formatDate(product.plantingDate)}</span>
                        </div>

                        <div className="detail-item">
                            <label>Expected Harvest:</label>
                            <span>{formatDate(product.expectedHarvestDate)}</span>
                        </div>

                        {product.harvestDate && (
                            <div className="detail-item">
                                <label>Actual Harvest:</label>
                                <span>{formatDate(product.harvestDate)}</span>
                            </div>
                        )}

                        {product.weight && (
                            <div className="detail-item">
                                <label>Weight:</label>
                                <span>{product.weight} {product.unit}</span>
                            </div>
                        )}

                        {product.qualityGrade && (
                            <div className="detail-item">
                                <label>Quality Grade:</label>
                                <span className={`quality-grade ${product.qualityGrade.toLowerCase()}`}>
                                    {product.qualityGrade}
                                </span>
                            </div>
                        )}

                        <div className="detail-item">
                            <label>Farmer:</label>
                            <span>{product.farmer?.firstName} {product.farmer?.lastName}</span>
                        </div>

                        <div className="detail-item">
                            <label>Farm:</label>
                            <span>{product.farmer?.farmName}</span>
                        </div>
                    </div>

                    {!product.harvestDate && (
                        <div className="action-buttons">
                            <button 
                                className="btn-primary"
                                onClick={markAsHarvested}
                            >
                                Mark as Harvested
                            </button>
                            <button 
                                className="btn-outline"
                                onClick={() => setShowHistoryForm(true)}
                            >
                                Add History Entry
                            </button>
                        </div>
                    )}
                </div>

                <div className="product-history">
                    <div className="history-header">
                        <h3>Product History</h3>
                        <button 
                            className="btn-outline"
                            onClick={() => setShowHistoryForm(true)}
                        >
                            Add Entry
                        </button>
                    </div>

                    {product.history && product.history.length > 0 ? (
                        <div className="history-timeline">
                            {product.history.map((entry, index) => (
                                <div key={index} className="history-item">
                                    <div className="history-timestamp">
                                        {formatDateTime(entry.timestamp)}
                                    </div>
                                    <div className="history-content">
                                        <strong>{entry.action}</strong>
                                        <p>{entry.description}</p>
                                        {entry.transactionHash && (
                                            <small className="transaction-hash">
                                                TX: {entry.transactionHash}
                                            </small>
                                        )}
                                    </div>
                                </div>
                            ))}
                        </div>
                    ) : (
                        <div className="empty-history">
                            <p>No history entries yet.</p>
                        </div>
                    )}
                </div>
            </div>

            {showHistoryForm && (
                <div className="modal-overlay">
                    <div className="modal">
                        <div className="modal-header">
                            <h2>Add History Entry</h2>
                            <button 
                                className="close-btn"
                                onClick={() => setShowHistoryForm(false)}
                            >
                                ×
                            </button>
                        </div>
                        <form onSubmit={handleHistorySubmit} className="history-form">
                            <div className="form-group">
                                <label>Action *</label>
                                <select
                                    name="action"
                                    value={historyForm.action}
                                    onChange={(e) => setHistoryForm(prev => ({
                                        ...prev,
                                        action: e.target.value
                                    }))}
                                    required
                                >
                                    <option value="">Select Action</option>
                                    <option value="PLANTED">Planted</option>
                                    <option value="WATERED">Watered</option>
                                    <option value="FERTILIZED">Fertilized</option>
                                    <option value="TREATED">Treated</option>
                                    <option value="INSPECTED">Inspected</option>
                                    <option value="HARVESTED">Harvested</option>
                                    <option value="PROCESSED">Processed</option>
                                    <option value="PACKAGED">Packaged</option>
                                    <option value="SHIPPED">Shipped</option>
                                    <option value="OTHER">Other</option>
                                </select>
                            </div>

                            <div className="form-group">
                                <label>Description *</label>
                                <textarea
                                    name="description"
                                    value={historyForm.description}
                                    onChange={(e) => setHistoryForm(prev => ({
                                        ...prev,
                                        description: e.target.value
                                    }))}
                                    required
                                    rows="4"
                                    placeholder="Describe what happened..."
                                />
                            </div>

                            <div className="form-actions">
                                <button
                                    type="button"
                                    className="btn-secondary"
                                    onClick={() => setShowHistoryForm(false)}
                                >
                                    Cancel
                                </button>
                                <button
                                    type="submit"
                                    className="btn-primary"
                                >
                                    Add Entry
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            )}
        </div>
    );
};

export default ProductView;
